TS.raw_templates['channel_share_dialog_request'] = "{{i18n_ns 'shared_channels'}}\n\n<div class=\"p-share_channel_dialog__contents\">\n\t\t<h1 class=\"share_channel_modal_heading\">\n\t\t\t{{#t}}Invitation \u00e0 rejoindre une cha\u00eene partag\u00e9e{{\/t}}<\/h1>\n\t\t<\/h1>\n\n\t\t<p>{{#t}}<strong>{inviting_team.name}<\/strong> souhaite partager une cha\u00eene avec votre espace de travail.{{\/t}}<\/p>\n\n\t\t{{> shared_channels_retention_disclaimer}}\n\n\t\t<h2 class=\"large_top_margin\">{{#t}}Invit\u00e9(e) par{{\/t}}<\/h2>\n\n\t\t <div class=\"p-share_channel_request_dialog__inviter small_bottom_margin\">\n\t\t\t<div class=\"c-unified_member c-unified_member--large\">\n\t\t\t\t<span class=\"member_image\">\n\t\t\t\t\t{{> member_preview_link_image (getMemberPreviewLinkImageArgs inviter 44 false true false)}}\n\t\t\t\t\t{{> member_type_external_badge size=44 team=inviting_team badge_size=(getTeamBadgeSize size) with_tooltip=true}}\n\t\t\t\t<\/span>\n\t\t\t\t<div class=\"c-unified_member__flex-container--large\">\n\t\t\t\t\t<div class=\"c-unified_member__name c-unified_member__name--large\">\n\t\t\t\t\t\t<span class=\"c-unified_member__display-name c-unified_member__display-name--large\">{{getPrefCompliantMemberName inviter}}<\/span>\n\t\t\t\t\t<\/div>\n\t\t\t\t\t<div class=\"c-unified_member__title c-unified_member__title--large\">{{getTeamDisplayName inviting_team}}<\/div>\n\t\t\t\t<\/div>\n\t\t\t<\/div>\n\t\t<\/div>\n\n\t\t{{#if custom_message}}\n\t\t<div class=\"bottom_margin\">\n\t\t\t{{#t note=\"Custom message for a shared channel invitation, wrapped in quotes\" custom_message=custom_message}}\n\t\t\t\t\u00ab\u00a0{custom_message}\u00a0\u00bb\n\t\t\t{{\/t}}\n\t\t<\/div>\n\t\t{{\/if}}\n\n\t\t<h2 class=\"large_top_margin small_bottom_margin\">{{#t}}Informations sur la cha\u00eene{{\/t}}<\/h2>\n\t\t<p class=\"greigh large_bottom_margin\">\n\t\t\t{{#t}}Vous pouvez modifier cela si vous le souhaitez. Les modifications que vous apportez affecteront uniquement <strong>{team.name}<\/strong>.{{\/t}}\n\t\t<\/p>\n\n\t\t<div class=\"c-alert c-alert--boxed c-alert--level_error large_bottom_margin hidden\" data-label=\"shared_channel_form_error\">\n\t\t\t<ts-icon class=\"c-alert__icon c-deprecated-icon c-icon--warning\"><\/ts-icon>\n\t\t\t<span data-label=\"shared_channel_form_error_label\"><\/span>\n\t\t<\/div>\n\n\t\t<div class=\"medium_bottom_margin\">\n\t\t\t<label class=\"radio\">\n\t\t\t\t{{#t}}<input type=\"radio\" name=\"channel_privacy\" value=\"public\" checked=\"checked\" aria-checked=\"true\">Publique <span class=\"normal small_left_margin\">Ouverte \u00e0 tous les membres de votre espace de travail<\/span>{{\/t}}\n\t\t\t<\/label>\n\t\t\t<label class=\"radio small_top_margin {{#if disable_private_option}}disabled ts_tip ts_tip_top ts_tip_float ts_tip_multiline ts_tip_hidden{{\/if}}\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"radio\"\n\t\t\t\t\tname=\"channel_privacy\"\n\t\t\t\t\tvalue=\"private\"\n\t\t\t\t\t{{#if disable_private_option}}\n\t\t\t\t\t\tdisabled=\"disabled\"\n\t\t\t\t\t\taria-disabled=\"true\"\n\t\t\t\t\t{{\/if}}\n\t\t\t\t\/>{{#t note=\"Channel privacy option\"}}Priv\u00e9e <span class=\"normal small_left_margin\">Cette cha\u00eene ne peut \u00eatre rejointe ou affich\u00e9e que sur invitation<\/span>{{\/t}}\n\n\t\t\t\t{{#if disable_private_option}}\n\t\t\t\t\t<span class=\"ts_tip_tip\">\n\t\t\t\t\t\t<span class=\"ts_tip_multiline_inner\">{{#t}}Seuls les propri\u00e9taires de l\u2019espace de travail<br\/> peuvent convertir une cha\u00eene publique en cha\u00eene priv\u00e9e{{\/t}}<\/span>\n\t\t\t\t\t<\/span>\n\t\t\t\t{{\/if}}\n\t\t\t<\/label>\n\n\t\t\t<div class=\"alert alert_warning top_margin hidden\" data-alert=\"private_channel\">\n\t\t\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t\t\t{{#t}}<strong>Remarque\u00a0:<\/strong> ce param\u00e8tre s\u2019applique uniquement \u00e0 <strong>{team.name}<\/strong>. Chaque espace de travail dans une cha\u00eene partag\u00e9e peut d\u00e9finir ses propres param\u00e8tres de confidentialit\u00e9.{{\/t}}\n\t\t\t<\/div>\n\t\t<\/div>\n\n\t\t<div class=\"medium_bottom_margin\">\n\t\t\t<label for=\"channel_name\" class=\"p-share_channel_dialog__input_label\">\n\t\t\t\t<span class=\"inline_block small_right_margin\">{{#t}}Nom{{\/t}}<\/span>\n\t\t\t<\/label>\n\t\t\t<div class=\"channel_modal_title_input_container no_margin\">\n\t\t\t\t<i class=\"channel_icon private_channel_item subtle_silver ts_icon_lock ts_icon hidden\" \/>\n\t\t\t\t<i class=\"channel_icon public_channel_item subtle_silver ts_icon_channel ts_icon\" \/>\n\t\t\t\t<input\n\t\t\t\t\tname=\"channel_name\"\n\t\t\t\t\ttype=\"text\"\n\t\t\t\t\tclass=\"lowercase title_input large_left_padding tiny_bottom_margin\"\n\t\t\t\t\tautocapitalize=\"off\"\n\t\t\t\t\tautocorrect=\"off\"\n\t\t\t\t\tautocomplete=\"off\"\n\t\t\t\t\tdata-input=\"channel_name\"\n\t\t\t\t\tdata-validation=\"channel_name=fix\"\n\t\t\t\t\tdata-validation-for=\"channel_name\"\n\t\t\t\t\tdata-validation-success=\"\"\n\t\t\t\t\tvalue=\"{{channel_name}}\"\n\t\t\t\t\/>\n\t\t\t<\/div>\n\t\t\t<span class=\"modal_input_note margin_auto\">\n\t\t\t\t{{#t}}Les noms doivent \u00eatre en lettres minuscules, sans espaces ni points et comporter moins de 22 caract\u00e8res.{{\/t}}\n\t\t\t<\/span>\n\t\t<\/div>\n\n\t\t<div>\n\t\t\t<label for=\"invite_members_input\" class=\"block align_left\">\n\t\t\t\t{{#t}}Inviter des collaborateurs <span class=\"normal\">(facultatif)<\/span>{{\/t}}\n\t\t\t<\/label>\n\t\t\t<div class=\"p-share_channel_dialog__invite_people_wrapper\">\n\t\t\t\t<div id=\"invite_people_picker\"><\/div>\n\t\t\t<\/div>\n\t\t<\/div>\n\n\t\t{{#if show_free_trial}}\n\t\t\t<div id=\"free_trial_container\" class=\"p-share_channel_dialog__free_trial_wrapper\">\n\t\t\t\t<div>\n\t\t\t\t\t<h3 class=\"p-share_channel_dialog__free_trial_heading black small_bottom_margin\">{{#t}}Pour accepter cette invitation, d\u00e9marrez un essai gratuit{{\/t}}<\/h3>\n\t\t\t\t\t<p class=\"p-share_channel_dialog__free_trial_paragraph\">{{#t}}Les cha\u00eenes partag\u00e9es sont une fonctionnalit\u00e9 en version b\u00eata incluses avec nos forfaits payants. Vous avez la possibilit\u00e9 de les tester gratuitement gr\u00e2ce \u00e0 l\u2019essai de 30\u00a0jours (gratuit) de notre forfait Standard. Vous n\u2019avez besoin d\u2019aucune carte bancaire.{{\/t}}<\/p>\n\t\t\t\t\t<p class=\"p-share_channel_dialog__free_trial_paragraph\">{{#t}}<b>Remarque\u00a0<\/b>: les cha\u00eenes partag\u00e9es sont <a href=\"https:\/\/get.slack.help\/hc\/en-us\/articles\/115004152843-Shared-channel-beta-security-policies-\" target=\"_blank\" data-clog-event=\"UPGRDEXP_FREE_TRIAL_FOR_SC\" data-clog-ui-element=\"link_beta_help_center\" data-clog-ui-action=\"click\" data-clog-ui-step=\"shared_channels_invitation_modal\">actuellement en version b\u00eata<\/a> et certaines fonctionnalit\u00e9s ne sont pas encore disponibles.{{\/t}}<\/p>\n\t\t\t\t\t<div class=\"position_relative\">\n\t\t\t\t\t\t<div id=\"free_trial_checkbox_alert\" class=\"p-share_channel_dialog__checkbox_alert sk_warning_red_bg position_absolute\"><\/div>\n\t\t\t\t\t\t<input type=\"checkbox\" id=\"free_trial_checkbox\" class=\"no_top_margin p-share_channel_dialog__free_trial_checkbox\" name=\"accept_free_trial\" value=\"accept_free_trial\">\n\t\t\t\t\t\t<label for=\"free_trial_checkbox\" class=\"p-share_channel_dialog__free_trial_checkbox_label no_bottom_margin inline_block\"><b>{{#t}}D\u00e9marrer un essai de 30\u00a0jours{{\/t}}<\/b><\/label>\n\t\t\t\t\t<\/div>\n\t\t\t\t<\/div>\n\t\t\t<\/div>\n\t\t{{\/if}}\n<\/div>\n";
TS.raw_templates['channel_share_dialog_error'] = "{{i18n_ns 'shared_channels'}}\n\n{{#if_equal error compare=\"invalid_target_domain\"}}\n    {{#t}}<strong>{target_domain}.slack.com ne semble pas \u00eatre utilis\u00e9.<\/strong> Si vous \u00eates certain(e) de l'avoir saisi correctement, veuillez demander \u00e0 votre contact de v\u00e9rifier son URL Slack.{{\/t}}\n{{\/if_equal}}\n\n{{#if_equal error compare=\"shared_channel_invites_disabled\"}}\n    {{#t}}<strong>L'espace de travail qui porte le nom de domaine {target_domain}.slack.com n'accepte pas les invitations \u00e0 rejoindre des cha\u00eenes partag\u00e9es.<\/strong> Nous vous sugg\u00e9rons de prendre contact avec son administrateur et de lui demander de modifier ce param\u00e8tre.{{\/t}}\n{{\/if_equal}}\n\n{{#if_equal error compare=\"not_in_shared_channels_beta\"}}\n    {{#t}}<strong>{target_domain}.slack.com n'a pas rejoint la version b\u00eata de la cha\u00eene partag\u00e9e.<\/strong> Celui-ci devra le faire avant que vous puissiez partager une cha\u00eene.{{\/t}}\n{{\/if_equal}}\n\n{{#if_equal error compare=\"target_team_is_enterprise\"}}\n    {{#t}}<strong>Tant que le programme est en phase b\u00eata, il est impossible de partager des cha\u00eenes avec des organisations qui utilisent la plateforme Entreprise Grid.<\/strong> Nous sommes pleinement conscients de cet inconv\u00e9nient, et nous nous employons \u00e0 y rem\u00e9dier dans les meilleurs d\u00e9lais. Consultez r\u00e9guli\u00e8rement les mises \u00e0 jour\u00a0!{{\/t}}\n{{\/if_equal}}\n";
TS.raw_templates['coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div id=\"coachmark\" class=\"normal hidden\">\n\t<div id=\"coachmark_callout\"><\/div>\n\t<div id=\"coachmark_interior\">\n\t\t{{#if has_close_btn}}\n\t\t<a class=\"coachmark_close_btn\">\n\t\t\t<i class=\"ts_icon ts_icon_times\"><\/i>\t\n\t\t<\/a>\n\t\t{{\/if}}\n\t\t{{! content divs are dynamically added depending on what type of coachmark is being shown }}\n\t\t{{#unless no_footer}}\n\t\t<div id=\"coachmark_footer\">\n\t\t\t<div class=\"coachmark_footer_text\">\n\t\t\t\t{{#t}}<span>Vous avez termin\u00e9\u00a0? <\/span><a class=\"coachmark_skip_link\">Cliquez ici pour ignorer<\/a>{{\/t}}\n\t\t\t<\/div>\n\t\t\t<a class=\"btn coachmark_got_it float_right hidden\">{{#t}}Bien compris\u00a0!{{\/t}}<\/a>\n\n\t\t\t<a class=\"btn coachmark_next_tip float_right\">{{#t}}Conseil suivant{{\/t}}<\/a>\n\t\t<\/div>\n\t\t{{\/unless}}\n\t<\/div>\n<\/div>\n";
TS.raw_templates['channels_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n{{!-- TODO: move to separate templates... so many conditions --}}\n<div id=\"channels_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"coachmark_contents\">\n\t\t<h2 class=\"black\">\n\t\t\t{{#t}}L'\u00e9change de messages se d\u00e9roule dans les cha\u00eenes{{\/t}}\n\t\t<\/h2>\n\t\t{{~#currentUserIsAdmin~}}\n\t\t\t<p>{{#t}}Les cha\u00eenes mettent de l'ordre et permettent de rechercher les conversations de votre \u00e9quipe. Organisez-les par th\u00e8me (#recherche), groupe de travail (#ventes) ou projet sp\u00e9cifique (#ventes-tv-annonce).{{\/t}}<\/p>\n\t\t{{~else~}}\n\t\t\t{{~#currentUserIsRA~}}\n\t\t\t\t<p>\n\t\t\t\t\t{{~#if in_single_private_channel~}}\n\t\t\t\t\t\t{{#t}}Les cha\u00eenes permettent d\u2019organiser les conversations de votre \u00e9quipe par th\u00e8me ou par groupe de travail. En tant qu\u2019invit\u00e9,{{\/t}}\n\t\t\t\t\t\t{{#t}}vous pouvez acc\u00e9der \u00e0 une cha\u00eene, <span class=\"no_wrap\"><ts-icon class=\"ts_icon_lock\"><\/ts-icon><strong>{first_group_channel_name}<\/strong><\/span>. Cette cha\u00eene priv\u00e9e n'est accessible que sur invitation.{{\/t}}\n\t\t\t\t\t{{~else if in_single_public_channel~}}\n\t\t\t\t\t\t{{#t}}Les cha\u00eenes permettent d\u2019organiser les conversations de votre \u00e9quipe par th\u00e8me ou par groupe de travail. En tant qu\u2019invit\u00e9, vous pouvez envoyer des messages dans une cha\u00eene, <strong>{first_listed_channel}<\/strong> et parcourir ses archives.{{\/t}}\n\t\t\t\t\t{{~else~}}\n\t\t\t\t\t\t{{#t}}Les cha\u00eenes permettent d\u2019organiser les conversations de votre \u00e9quipe par th\u00e8me ou par groupe de travail. En tant qu\u2019invit\u00e9, vous pouvez envoyer des messages dans les cha\u00eenes figurant sur cette liste et parcourir leurs archives.{{\/t}}\n\t\t\t\t\t{{~\/if~}}\n\t\t\t\t<\/p>\n\t\t\t{{~else~}}\n\t\t\t\t<p>{{#t}}Les cha\u00eenes mettent de l'ordre et permettent de rechercher les conversations de votre \u00e9quipe. Elles sont organis\u00e9es par th\u00e8me (#recherche), groupe de travail (#ventes) ou par projet sp\u00e9cifique (#vente-tv-annonce).{{\/t}}<\/p>\n\t\t\t{{~\/currentUserIsRA~}}\n\t\t{{~\/currentUserIsAdmin~}}\n\t<\/div>\n<\/div>\n";
TS.raw_templates['intl_channel_names_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div id=\"intl_channel_names_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"coachmark_contents\">\n\t\t{{#t}}\n\t\t<h2 class=\"black\">Vos cha\u00eenes, votre langue<\/h2>\n\t\t<p>\u00c0 savoir\u00a0: Les noms de cha\u00eenes peuvent d\u00e9sormais comprendre des caract\u00e8res de nombreuses langues diff\u00e9rentes.<\/p>\n\t\t{{\/t}}\n\t<\/div>\n<\/div>\n";
TS.raw_templates['invites_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div id=\"invites_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"coachmark_contents\">\n\t\t<h2 class=\"black\">{{#t}}Vous \u00eates pr\u00eat(e), ajoutez simplement des coll\u00e8gues{{\/t}}<\/h2>\n\t\t{{~#currentUserIsAdmin~}}\n\t\t\t<p>{{#t}}Pour profiter au mieux de Slack, invitez d'autres coll\u00e8gues et commencez \u00e0 vous envoyer des messages\u00a0!{{\/t}}<\/p>\n\t\t{{~else~}}\n\t\t\t<p>{{#t}}Pour profiter au mieux de Slack, invitez d'autres coll\u00e8gues et commencez \u00e0 vous envoyer des messages\u00a0!{{\/t}}<\/p>\n\t\t{{~\/currentUserIsAdmin~}}\n\t<\/div>\n<\/div>\n";
TS.raw_templates['direct_messages_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div id=\"direct_messages_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"coachmark_contents\">\n\t\t<h2 class=\"black\">{{#t}}Envoyer des messages directs{{\/t}}<\/h2>\n\t\t{{~#currentUserIsAdmin~}}\n\t\t\t<p>{{#t}}Vous avez besoin d'envoyer rapidement un mot \u00e0 une seule personne ou \u00e0 un petit groupe de collaborateurs dans votre \u00e9quipe\u00a0? C'est pr\u00e9cis\u00e9ment la fonction des messages directs.{{\/t}}<\/p>\n\t\t{{~else~}}\n\t\t\t<p>{{#t}}Vous avez besoin d'envoyer rapidement un mot \u00e0 une seule personne ou \u00e0 un petit groupe de collaborateurs dans votre \u00e9quipe\u00a0?{{\/t}}\n\t\t\t{{~#currentUserIsRA~}}\n\t\t\t\t{{~#if_equal channels_count compare=0}}\n\t\t\t\t\t{{#t}}Vous pouvez envoyer directement des messages \u00e0 toute personne appartenant \u00e0 la m\u00eame cha\u00eene que vous.{{\/t}}\n\t\t\t\t{{~else~}}\n\t\t\t\t\t{{~#if_gt groups_count compare=0}}\n\t\t\t\t\t\t{{#t}}Vous pouvez envoyer directement un message \u00e0 toute personne appartenant aux m\u00eames cha\u00eenes ou groupes que vous.{{\/t}}\n\t\t\t\t\t{{~else}}\n\t\t\t\t\t\t{{#t}}Vous pouvez envoyer des messages \u00e0 n'importe quel utilisateur qui appartient \u00e0 la m\u00eame\/aux m\u00eames {channels_count, plural, =1 {cha\u00eene} other {cha\u00eenes}} que vous.{{\/t}}\n\t\t\t\t\t{{~\/if_gt~}}\n\t\t\t\t{{~\/if_equal~}}\n\t\t\t{{~else}}\n\t\t\t\t{{#t}}Les messages directs sont parfaits pour cela.{{\/t}}\n\t\t\t{{~\/currentUserIsRA~}}\n\t\t\t<\/p>\n\t\t{{~\/currentUserIsAdmin~}}\n\t<\/div>\n<\/div>\n";
TS.raw_templates['highlights_feedback_coachmark'] = "{{i18n_ns 'highlights_coachmark'}}\n\n<div id=\"highlights_feedback_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"coachmark_contents\">\n\t\t<h2 class=\"black\">{{#t}}Ajustez vos messages-cl\u00e9s{{\/t}}<\/h2>\n\t\t<p>{{#t}}Lorsqu'un message en surbrillance est particuli\u00e8rement utile (ou pas), laissez un court commentaire et Slack proc\u00e8dera \u00e0 des ajustements.{{\/t}}<\/p>\n\t<\/div>\n<\/div>\n";
TS.raw_templates['recent_mentions_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div id=\"recent_mentions_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"small_coachmark_contents\">\n\t\t<span>{{#t}}<strong class=\"black\">Cliquez<\/strong> pour en savoir plus sur les @ mentions.{{\/t}}<\/span>\n\t<\/div>\n\t<div class=\"coachmark_contents\">\n\t\t{{#t}}\n\t\t<h2 class=\"black\">Toutes vos @ mentions en un seul et m\u00eame endroit<\/h2>\n\t\t<p>Chaque fois que quelqu'un mentionne votre nom ou un de vos mots cl\u00e9s, le message appara\u00eetra ici.<\/p>\n\t\t{{\/t}}\n\t<\/div>\n<\/div>\n";
