TS.raw_templates['account_notifications_channel_overrides_row'] = "{{i18n_ns \"account_notifications\"}}\n\n<tr id=\"channel_override_row_{{channel.id}}\" class=\"channel_override_row {{#if channel.is_muted}}muted{{\/if}}\">\n\t<td class=\"channel_details\">\n\t\t<span class=\"no_wrap\">\n\t\t\t<i class=\"ts_icon ts_icon_bell_slash mute_icon\"><\/i>\n\t\t\t{{~#if channel.is_group~}}\n\t\t\t\t{{~#if channel.is_mpim~}}\n\t\t\t\t\t<i class=\"ts_icon ts_icon_multiparty_dm_{{mpimMemberCount channel.channel}}\"><\/i>\n\t\t\t\t{{~else~}}\n\t\t\t\t\t<i class=\"ts_icon ts_icon_lock_o group_lock_prefix\"><\/i>\n\t\t\t\t{{~\/if~}}\n\t\t\t{{~\/if~}}\n\t\t\t{{~#if channel.is_channel~}}\n\t\t\t\t<span class=\"channel_hash_prefix\">#<\/span>\n\t\t\t{{~\/if~}}\n\t\t\t<strong>{{channel.name}}<\/strong>\n\t\t\t<span class=\"pref_saved inline_block small_left_margin\"><i class=\"ts_icon ts_icon_check_circle_o\"><\/i> {{#t}}Enregistr\u00e9{{\/t}}<\/span>\n\t\t<\/span>\n\n\t\t<label class=\"checkbox no_margin normal\">\n\t\t\t<input id=\"muting_cb_{{channel.id}}\" {{#if channel.is_muted}}checked=\"checked\"{{\/if}} type=\"checkbox\" data-id=\"{{channel.id}}\" data-pref=\"muting\" class=\"small_right_margin\">\n\t\t\t{{#if channel.is_mpim}}\n\t\t\t\t{{#t}}D\u00e9sactiver les notifications de cette conversation{{\/t}}\n\t\t\t{{else}}\n\t\t\t\t{{#if channel.is_group}}\n\t\t\t\t\t{{#t}}D\u00e9sactiver les notifications de cette cha\u00eene priv\u00e9e{{\/t}}\n\t\t\t\t{{else}}\n\t\t\t\t\t{{#t}}D\u00e9sactiver les notifications de cette cha\u00eene{{\/t}}\n\t\t\t\t{{\/if}}\n\t\t\t{{\/if}}\n\t\t<\/label>\n\n\t\t<label id=\"at_channel_suppressed_{{channel.id}}\" class=\"hidden checkbox no_margin normal {{#if channel.is_muted}}disabled{{\/if}}\">\n\t\t\t<input id=\"at_channel_suppressed_cb_{{channel.id}}\" checked=\"checked\" {{#if channel.is_muted}}disabled{{\/if}} type=\"checkbox\" data-id=\"{{channel.id}}\" data-pref=\"at_channel_suppression\" class=\"small_right_margin\">\n\t\t\t{{#if channel.is_group}}\n\t\t\t\t{{#t at_label=(atLabel 'channel')}}Supprimer les mentions <strong>{at_label}<\/strong>{{\/t}}\n\t\t\t{{else}}\n\t\t\t\t{{#isChannelRequired channel}}\n\t\t\t\t\t{{#t at_label=(atLabel 'everyone')}}Supprimer les mentions <strong>{at_label}<\/strong>{{\/t}}\n\t\t\t\t{{else}}\n\t\t\t\t\t{{#t at_label=(atLabel 'channel')}}Supprimer les mentions <strong>{at_label}<\/strong>{{\/t}}\n\t\t\t\t{{\/isChannelRequired}}\n\t\t\t{{\/if}}\n\t\t\t<span id=\"at_channel_suppressed_mobile_qualifier_{{channel.id}}\">{{#t}}(sur appareil mobile){{\/t}}<\/span>\n\t\t\t<span id=\"at_channel_suppressed_desktop_qualifier_{{channel.id}}\">{{#t}}(sur ordinateur){{\/t}}<\/span>\n\t\t\t<span id=\"at_channel_suppressed_disabled_explain_{{channel.id}}\" class=\"hidden\">\n\t\t\t\t<br>{{#t}}(d\u00e9sactiv\u00e9 en raison des <strong id=\"at_channel_suppressed_disabled_explain_tip_link_{channel.id}\" class=\"{channel.is_muted, select, true{ts_tip_hide}other{}} ts_tip ts_tip_top ts_tip_multiline ts_tip_float\">param\u00e8tres d\u2019\u00e9quipe<span class=\"ts_tip_tip\"><span class=\"ts_tip_multiline_inner\"><\/span><\/span><\/strong>){{\/t}}\n\t\t\t<\/span>\n\t\t<\/label>\n\n\t<\/td>\n\n\t<td class=\"radio_cell\"><input type=\"radio\" id=\"desktop_everything_{{channel.id}}\" name=\"desktop_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"desktop\" value=\"everything\" {{#if channel.is_muted}}{{#t}}d\u00e9sactiv\u00e9{{\/t}}{{\/if}}><\/td>\n\t<td class=\"radio_cell\"><input type=\"radio\" id=\"desktop_mentions_{{channel.id}}\" name=\"desktop_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"desktop\" value=\"mentions\" {{#if channel.is_muted}}{{#t}}d\u00e9sactiv\u00e9{{\/t}}{{\/if}}><\/td>\n\t<td class=\"radio_cell\"><input type=\"radio\" id=\"desktop_nothing_{{channel.id}}\" name=\"desktop_{{channel.id}}\" data-device=\"desktop\" data-id=\"{{channel.id}}\" value=\"nothing\" {{#if channel.is_muted}}{{#t}}d\u00e9sactiv\u00e9{{\/t}}{{\/if}}><\/td>\n\n\t<td class=\"radio_cell\"><input type=\"radio\" id=\"mobile_everything_{{channel.id}}\" name=\"mobile_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"mobile\" value=\"everything\" {{#if channel.is_muted}}{{#t}}d\u00e9sactiv\u00e9{{\/t}}{{\/if}}><\/td>\n\t<td class=\"radio_cell\"><input type=\"radio\" id=\"mobile_mentions_{{channel.id}}\" name=\"mobile_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"mobile\" value=\"mentions\" {{#if channel.is_muted}}{{#t}}d\u00e9sactiv\u00e9{{\/t}}{{\/if}}><\/td>\n\t<td class=\"radio_cell\"><input type=\"radio\" id=\"mobile_nothing_{{channel.id}}\" name=\"mobile_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"mobile\" value=\"nothing\" {{#if channel.is_muted}}{{#t}}d\u00e9sactiv\u00e9{{\/t}}{{\/if}}><\/td>\n\n\t<td class=\"align_center\"><i class=\"ts_icon ts_icon_times_circle revert_to_default\" data-id=\"{{channel.id}}\" data-toggle=\"tooltip\" title=\"{{#t channel_name=channel.name}}Effacer les param\u00e8tres personnalis\u00e9s de notification de {channel.is_channel, select, true{#}other{}}{channel_name}{{\/t}}\"><\/i><\/td>\n<\/tr>\n";
TS.raw_templates['sidebar_theme_css'] = "{!--\n * This file is used to theme sidebar elements that haven't been ported to React.\n * It is deprecated by js\/modern\/components\/channel-sidebar\/theme.jsx\n * Yes: this needs to be improved, but this can't be removed until\n * the team menu header block is converted to React (it's also themed).\n --}\n<style type=\"text\/css\" id=\"sidebar_theme_css\">\n\n\t\/* Column Background *\/\n\t.client_channels_list_container {\n\t\tbackground: {{theme.column_bg}};\n\t}\n\n\t\/* Menu Background *\/\n\t#team_menu {\n\t\tcolor: {{theme.text_color}};\n\t\ttext-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);\n\t}\n\t#team_menu,\n\t#team_menu_overlay {\n\t\tborder-color: {{theme.column_bg}};\n\t}\n\tbody:not(.loading) #team_menu:hover,\n\tbody:not(.loading) #team_menu.active {\n\t\tbackground: {{theme.menu_bg}};\n\t\tborder-color: {{theme.menu_bg}};\n\t}\n\n\t\/* Text Color *\/\n\t#team_menu:before,\n\t#team_menu .notifications_menu_btn,\n\t#team_menu .team_name_caret,\n\t#team_menu_user_name {\n\t\tcolor: {{theme.text_color}} !important;\n\t\topacity: 0.6;\n\t}\n\t#team_menu_user,\n\t#team_menu_user_details .current_status ts-icon {\n\t\tcolor: {{theme.text_color}};\n\t}\n\n\t#team_menu:hover:before,\n\t#team_menu.active:before,\n\t#team_menu:hover .team_name_caret,\n\t#team_menu.active .team_name_caret,\n\t#team_menu:hover #team_menu_user_name,\n\t#team_menu:hover .current_user_current_status,\n\t#team_menu .team_name_caret.focus-ring {\n\t\topacity: 1;\n\t}\n\n\t\/* Presence *\/\n\t#team_menu #presence.active:before {\n\t\tcolor: {{theme.active_presence}};\n\t\topacity: 1;\n\t}\n\n\t\/* Badge + Mentions bar *\/\t\t\n \tts-jumper ol li .unread_count {\n \t\tbackground: {{theme.badge}};\t\t\n \t}\n\n\t\/* No text shadows *\/\n\t#team_menu {\n\t\ttext-shadow: none !important;\n\t}\n<\/style>\n";
TS.raw_templates['signin_find_team'] = "{{i18n_ns 'find_team'}}\n\n{{#unlessOurApp}}<a data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"action=nav_logo,trigger=find_team\" class=\"logo_link\" href=\"\/\">{{\/unlessOurApp}}<img id=\"slack_logo\" src=\"{{versioned_slack_logo_240}}\" width=\"120\" height=\"37\" title=\"Slack\">{{#unlessOurApp}}<\/a>{{\/unlessOurApp}}\n\n<div class=\"header_error_message hidden\"><\/div>\n\n<div id=\"form_body\" class=\"fs_split_body\">\n\t<h1>\n\t\t{{#if is_second_step}}\n\t\t\t{{#t}}Trouver un autre espace de travail{{\/t}}\n\t\t{{else}}\n\t\t\t{{#t}}Trouver votre espace de travail{{\/t}}\n\t\t{{\/if}}\n\t<\/h1>\n\t<label id=\"email_label\">{{#t}}<span class=\"normal\">Saisissez votre <\/span>adresse e-mail{{\/t}}<\/label>\n\n\t<div class=\"input_wrapper email\">\n\t\t<textarea type=\"text\" placeholder=\"{{exampleEmail 'you'}}\" id=\"email_input\" autocomplete=\"off\" spellcheck=\"false\" aria-labelledby=\"email_label email_rule_label\" data-email-healing=\"true\"><\/textarea>\n\t<\/div>\n\n\t<p id=\"email_rule_label\" class=\"subtle_silver\">{{#t}}Vous pouvez essayer jusqu'\u00e0 10\u00a0adresses e-mail, s\u00e9par\u00e9es par une virgule{{\/t}}<\/p>\n\t<p class=\"error_message\"><\/p>\n\t<div class=\"desc\">{{#t}}Nous vous enverrons un e-mail avec des liens vous permettant de vous connecter aux espaces de travail Slack associ\u00e9s \u00e0 l'adresse ou aux adresses saisie(s) ici.{{\/t}}<\/div>\n<\/div>\n\n<div id=\"submit_footer\" class=\"fs_split_footer centered\">\n\t<button id=\"submit_btn\" type=\"submit\" class=\"btn btn_large ladda-button submit_setting\" data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"click_target=find_team_send_signin_email\" data-style=\"expand-right\" aria-disabled=\"true\" disabled=\"true\">\n\t\t<span id=\"submit_btn_label_single\" class=\"ladda-label\">{{#t}}Envoyez-moi un e-mail de connexion{{\/t}}<\/span>\n\t\t<span id=\"submit_btn_label_plural\" class=\"ladda-label hidden\">{{#t}}Envoyez-moi des e-mails de connexion{{\/t}}<\/span>\n\t<\/button>\n\t{{#if reenter_signup_flow}}\n\t\t<p class=\"normal_margin reenter_signup_flow subtle_silver\"><ts-icon class=\"ts_icon_arrow_left\"><\/ts-icon>{{#t}}Vous pouvez aussi <a href=\"\/create\" class=\"clear_blue\" data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"action=go_back_create_team,trigger=find_team\"> revenir en arri\u00e8re et cr\u00e9er un nouvel espace de travail<\/a>{{\/t}}<\/p>\n\t{{\/if}}\n\t{{#if go_back_team_select}}\n\t\t<p class=\"normal_margin go_back_team_select subtle_silver\"><ts-icon class=\"ts_icon_arrow_left\"><\/ts-icon>{{#t}}Vous pouvez aussi, <a href=\"\/signin\/find\" class=\"clear_blue\" data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"action=go_back_select_team,trigger=find_team\"> revenir en arri\u00e8re et s\u00e9lectionner un espace de travail<\/a>{{\/t}}<\/p>\n\t{{\/if}}\n<\/div>\n<div class=\"fs_split_footer\"><\/div>\n";
TS.raw_templates['signin_find_team_email_sent'] = "{{i18n_ns 'find_team'}}\n\n{{#unlessOurApp}}<a data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"action=nav_logo,trigger=find_team\" class=\"logo_link\" href=\"\/\">{{\/unlessOurApp}}<img id=\"slack_logo\" src=\"{{versioned_slack_logo_240}}\" width=\"120\" height=\"137\" title=\"Slack\">{{#unlessOurApp}}<\/a>{{\/unlessOurApp}}\n\n<div id=\"sent_invite_body\" class=\"fs_split_body\">\n\t{{#if multi}}\n\t<h1>{{#t}}Rappels envoy\u00e9s\u00a0!{{\/t}}<\/h1>\n\t<p class=\"desc\">{{#t}}Si des comptes Slack sont associ\u00e9s \u00e0 {email}, vous recevrez rapidement un e-mail de notre part.{{\/t}}<\/p>\n\t{{else}}\n\t<h1>{{#t}}Rappel envoy\u00e9\u00a0!{{\/t}}<\/h1>\n\t<p class=\"desc\">{{#t}}Si un compte Slack est associ\u00e9 \u00e0 {email}, vous recevrez rapidement un e-mail de notre part.{{\/t}}<\/p>\n\t{{\/if}}\n\n\t{{#t}}\n\t<p class=\"desc small_bottom_margin\">Vous ne trouvez pas votre \u00e9quipe\u00a0?<\/p>\n\t<p class=\"desc\">Vous pouvez <strong><a href=\"\/signin\/find\" data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"action=try_another_email,trigger=find_team\">essayer une autre adresse e-mail<\/a><\/strong> ou nous contacter \u00e0 l'adresse <a href=\"mailto:feedback@slack.com\">feedback@slack.com<\/a>.<\/p>\n\t{{\/t}}\n<\/div>\n\n<div class=\"fs_split_footer\"><\/div>\n<div class=\"fs_split_footer subtle_silver\">\n{{#isOurApp}}\n\t{{#t}}(Lorsque vous l'avez re\u00e7u, <a href=\"\/signin\" data-clog-event=\"WEBSITE_CLICK\" data-clog-params=\"action=try_sign_in,trigger=find_team\" class=\"bold\">cliquez ici pour vous connecter<\/a>.){{\/t}}\n{{else}}\n\t{{#t}}(Vous pouvez probablement fermer cette page maintenant.){{\/t}}\n{{\/isOurApp}}\n<\/div>";
TS.raw_templates['invite_single_page_form'] = "\n{{i18n_ns 'invite'}}\n\n\n<div class= \"fs_grey large_top_padding col span_3_of_6 margin_auto float_none\">\n\n<div id=\"ratelimit_alert\" class=\"header_error_message hidden bottom_margin\">\n    <h3>{{#t note=\"This refers to a rate limit we have for filling out this form\"}}Houl\u00e0, vous allez trop vite\u00a0!{{\/t}}<\/h3>\n    <p class=\"no_margin\">{{#t}}Ralentissez un peu. Vous aurez peut-\u00eatre plus de r\u00e9ussite si vous r\u00e9essayez dans une minute.{{\/t}}<\/p>\n<\/div>\n\n<div id=\"invite_form_body\">\n    <h1>{{#t}}Rejoindre l\u2019espace de travail Slack pour {team_name}{{\/t}}<\/h1>\n\n    <div class=\"names_container\" id=\"names_container\">\n        <label>{{#t}}Nom complet{{\/t}}<\/label>\n        <div class=\"name_field\" id=\"real_name_wrapper\">\n            <input type=\"text\" name=\"full_name\" placeholder=\"{{#t}}Nom complet{{\/t}}\" id=\"invite_real_name\" autocomplete=\"off\" data-qa=\"real_name_field\" {{#if real_name}}value=\"{{real_name}}\"{{\/if}} data-validation=\"membername=required\" data-validation-for=\"real_name_profile_field_warnings\" data-validation-success\/>\n            <label for=\"real_name_profile_field_warnings\"><span class=\"validation_message\"><\/span><\/label>\n        <\/div>\n    <\/div>\n\n    <div id=\"username_container\">\n        <label for=\"display_name_overlay\">{{#t}}Nom d\u2019affichage (facultatif){{\/t}}<\/label>\n            <div class=\"input_wrapper display_name\">\n            <p id=\"display_name_overlay\" class=\"input_overlay\"><\/p>\n            <input type=\"text\" name=\"display_name\" id=\"invite_display_name\" autocomplete=\"off\" data-qa=\"display_name_field\" placeholder=\"{{#if display_name_placeholder_prefill}}{{display_name_placeholder_prefill}}{{else}}{{#t}}Nom d'affichage{{\/t}}{{\/if}}\" data-validation=\"membername\" data-validation-for=\"display_name_profile_field_warnings\" data-validation-success\/>\n        <\/div>\n        <label for=\"display_name_profile_field_warnings\"><span class=\"validation_message\"><\/span><\/label>\n        <p class=\"sk_dark_grey\">{{#t}}Par d\u00e9faut, Slack affiche votre nom complet. Vous pouvez cependant choisir un nom plus court si vous le souhaitez.{{\/t}}<\/p>\n\n        {{#if custom_username_policy}}\n            <div id=\"custom_username_policy\" class=\"large_bottom_margin\">\n                <p class=\"bold small_bottom_margin\">{{#t}}R\u00e8gles personnalis\u00e9es pour cet espace de travail\u00a0:{{\/t}}<\/p>\n                <p class=\"sk_dark_grey no_bottom_margin\">{{custom_username_policy}}<\/p>\n            <\/div>\n        {{\/if}}\n    <\/div>\n\n    <div id=\"password_container\">\n        <label for=\"password\">{{#t}}Mot de passe (requis){{\/t}}<\/label>\n        <input type=\"password\" id=\"invite_password\" data-qa=\"password-field\" placeholder=\"{{#t}}Mot de passe{{\/t}}\" name=\"password\"\/>\n        <p class=\"error_message small_bottom_margin\" id=\"password_error_message\"><\/p>\n\n        <p class=\"sk_dark_grey\">{{#t}}Les mots de passe doivent contenir au moins 6\u00a0caract\u00e8res et ne doivent pas \u00eatre faciles \u00e0 deviner. \u00c9vitez par exemple \u00ab\u00a0mot de passe\u00a0\u00bb, \u00ab\u00a0123456\u00a0\u00bb ou \u00ab\u00a0abcdef\u00a0\u00bb.{{\/t}}<\/p>\n    <\/div>\n\n    {{#if collect_guest_user_role}}\n        {{> invite_inc_guest_user_role}}\n    {{\/if}}\n\n    <div id=\"submit\">\n        {{! show europe GDPR-friendly radio buttons #feat-gdpr-acct-create}}\n        {{#if is_europe}}\n            <div id=\"email_misc_radio_button\">\n                <p>{{#t}}<strong>Pouvons-nous vous envoyer un e-mail avec des conseils, des nouvelles et des offres de Slack\u00a0?<\/strong>{{\/t}}<\/p>\n                <label class=\"radio normal\">\n                    <input type=\"radio\" id=\"email_misc\" name=\"emailok\" value=\"true\" data-qa=\"email_radio_dial_true\" \/>{{#t note=\"A radio dial option for opting into Slack email\"}}Oui{{\/t}}\n                <\/label>\n                <label class=\"radio normal\">\n                    <input type=\"radio\" id=\"email_misc_false\" name=\"emailok\" value=\"false\" data-qa=\"email_radio_dial_false\" \/>{{#t note=\"A radio dial option for opting out of Slack email\"}}Non merci{{\/t}}\n                <\/label>\n            <\/div>\n        {{else}}\n            <div id=\"email_checkbox\">\n                <input class=\"spf_input\" type=\"checkbox\" id=\"email_misc\" data-qa=\"email_checkbox\" {{#if uncheck_email}}{{else}}checked{{\/if}}>\n                <label class=\"spf_label\" for=\"email_misc\">{{#t}}Je suis d\u2019accord pour recevoir des e-mails sur le service Slack.{{\/t}}<\/label>\n            <\/div>\n        {{\/if}}\n\n        <div class=\"align_center\">\n            <button id=\"submit_btn\" type=\"submit\" class=\"{{#unless email_reminded}}disabled{{\/unless}} btn btn_large ladda-button submit_setting\" data-style=\"expand-right\" data-qa=\"submit_button\">\n                <span class=\"ladda-label\">{{#t}}Suivant{{\/t}}<\/span>\n            <\/button>\n        <\/div>\n    <\/div>\n\n<\/div>\n<\/div>\n";
TS.raw_templates['invite_sso_form'] = "{{i18n_ns \"invite\"}}\n\n<div class=\"fs_split_header\"><\/div>\n\n<div class= \"fs_grey large_top_padding col span_3_of_6 margin_auto float_none\">\n<div id=\"invite_sso_form_body\" class=\"fs_split_body\">\n    <h1>{{#t}}Rejoindre {team_name}{{\/t}}<\/h1>\n    <div id=\"sso_container\">\n        {{#if_equal auth_mode compare=\"google\"}}\n            {{#if sso_required}}\n                <p>{{#t}}Pour cet espace de travail, vous devez vous inscrire avec votre compte Google <strong>@{email_domain}.<\/strong>{{\/t}}<\/p>\n            {{else}}\n                {{#if domain_match}}\n                    <p>\n                        {{#if_gt allowed_domains_count compare=1}}\n                            {{#t}}Vous pouvez vous inscrire \u00e0 cet espace de travail en utilisant votre compte<strong>Google Apps<\/strong>.{{\/t}}\n                        {{else}}\n                            {{#t}}Cet espace de travail vous permet de vous connecter avec votre <strong>compte Google<\/strong> @{google_sso_domain}.{{\/t}}\n                        {{\/if_gt}}\n                    <\/p>\n                {{\/if}}\n            {{\/if}}\n            {{#or sso_required domain_match}}\n                <p>\n                    <a href=\"{{auth_url}}\" class=\"btn btn_info btn_large btn_with_icon full_width\">\n                        <i class=\"ts_icon ts_icon_google\"><\/i>\n                        {{#t}}S\u2019inscrire avec Google{{\/t}}\n                    <\/a>\n                <\/p>\n            {{\/or}}\n            {{#unless sso_required}}\n                {{#if domain_match}}\n                    <p class=\"large_top_margin\">{{#t}}Vous pouvez \u00e9galement vous inscrire avec un <strong>nom d\u2019utilisateur<\/strong> et un <strong>mot de passe<\/strong>.{{\/t}}<\/p>\n                    <p id=\"no_sso_btn\" class=\"btn btn_large full_width ladda-button\">\n                        <span class=\"ladda-label\">{{#t}}Saisir le nom d\u2019utilisateur et le mot de passe{{\/t}}<\/span>\n                    <\/p>\n                {{\/if}}\n            {{\/unless}}\n        {{else}}\n            <p>\n                {{#if sso_required}}\n                    {{#t}}Pour cet espace de travail, vous devez vous connecter avec votre compte {saml_provider_label}.{{\/t}}\n                {{else}}\n                    {{#t}}Cet espace de travail vous permet de vous connecter avec votre compte {saml_provider_label}.{{\/t}}\n                {{\/if}}\n            <\/p>\n            <p>\n                <a href=\"{{invite_sso_url}}?action=invite&redir=%2Finvite%2F{{invite_code}}\" class=\"btn btn_info btn_large btn_with_icon full_width\">\n                    <i class=\"ts_icon ts_icon_key\"><\/i>\n                    {{#t}}Se connecter avec {saml_provider_label}{{\/t}}\n                <\/a>\n            <\/p>\n            {{#unless sso_required}}\n                <p class=\"large_top_margin\">{{#t}}Vous pouvez \u00e9galement vous inscrire avec un <strong>nom d\u2019utilisateur<\/strong> et un <strong>mot de passe<\/strong>.{{\/t}}<\/p>\n                <p id=\"no_sso_btn\" class=\"btn btn_large full_width ladda-button\">\n                    <span class=\"ladda-label\">{{#t}}Saisir le nom d\u2019utilisateur et le mot de passe{{\/t}}<\/span>\n                <\/p>\n            {{\/unless}}\n        {{\/if_equal}}\n    <\/div>\n<\/div>\n<\/div>\n\n<div class=\"fs_split_footer\"><\/div>\n<div class=\"fs_split_footer\"><\/div>\n";
TS.raw_templates['invite_name_form'] = "{{i18n_ns 'invite'}}\n\n<div class=\"fs_split_header\"><\/div>\n\n<div id=\"ratelimit_alert\" class=\"header_error_message hidden bottom_margin\">\n    <h3>{{#t note=\"This refers to a rate limit we have for filling out this form\"}}Houl\u00e0, vous allez trop vite\u00a0!{{\/t}}<\/h3>\n    <p class=\"no_margin\">{{#t}}Ralentissez un peu. Vous aurez peut-\u00eatre plus de r\u00e9ussite si vous r\u00e9essayez dans une minute.{{\/t}}<\/p>\n<\/div>\n\n<div id=\"invite_form_body\" class=\"fs_split_body\">\n    <h1>{{#t}}Rejoindre l\u2019espace de travail Slack pour <span class=\"bold\">{team_name}<\/span>{{\/t}}<\/h1>\n\n    <div class=\"names_container no_top_padding bottom_padding\" id=\"names_container\">\n        <label>{{#t}}Nom complet{{\/t}}<\/label>\n        <div class=\"name_field\" id=\"real_name_wrapper\">\n            <input type=\"text\" name=\"full_name\" placeholder=\"{{#t}}Nom complet{{\/t}}\" id=\"invite_real_name\" autocomplete=\"off\" data-qa=\"real_name_field\" {{#if real_name}}value=\"{{real_name}}\"{{\/if}} data-validation=\"membername=required\" data-validation-for=\"real_name_profile_field_warnings\" data-validation-success\/>\n            <label for=\"real_name_profile_field_warnings\"><span class=\"validation_message\"><\/span><\/label>\n        <\/div>\n    <\/div>\n\n    <div id=\"username_container\">\n        <label for=\"display_name_overlay\">{{#t}}Nom d\u2019affichage (facultatif){{\/t}}<\/label>\n            <div class=\"input_wrapper display_name\">\n            <p id=\"display_name_overlay\" class=\"input_overlay\"><\/p>\n            <input type=\"text\" name=\"display_name\" id=\"invite_display_name\" autocomplete=\"off\" data-qa=\"display_name_field\" placeholder=\"{{#if display_name_placeholder_prefill}}{{display_name_placeholder_prefill}}{{else}}{{#t}}Nom d'affichage{{\/t}}{{\/if}}\" data-validation=\"membername\" data-validation-for=\"display_name_profile_field_warnings\" data-validation-success\/>\n        <\/div>\n        <label for=\"display_name_profile_field_warnings\"><span class=\"validation_message\"><\/span><\/label>\n        <p class=\"subtle_silver\">{{#t}}Par d\u00e9faut, Slack affiche votre nom complet lorsque quelqu\u2019un mentionne votre nom. Vous pouvez choisir un nom plus court si vous le souhaitez.{{\/t}}<\/p>\n\n        {{#if custom_username_policy}}\n            <div id=\"custom_username_policy\" class=\"large_bottom_margin\">\n                <p class=\"bold small_bottom_margin\">{{#t}}R\u00e8gles personnalis\u00e9es pour cet espace de travail\u00a0:{{\/t}}<\/p>\n                <p class=\"no_bottom_margin\">{{custom_username_policy}}<\/p>\n            <\/div>\n        {{\/if}}\n    <\/div>\n<\/div>\n\n{{#if collect_user_role}}\n    <div id=\"submit\" class=\"fs_split_footer full_width\">\n        <div id=\"email_checkbox\" class=\"align_left no_top_margin large_bottom_margin\">\n            <input type=\"checkbox\" id=\"email_misc\" data-qa=\"email_checkbox\" {{#if uncheck_email}}{{else}}checked{{\/if}}>\n            <label for=\"email_misc\">{{#t}}Je suis d\u2019accord pour recevoir des e-mails sur le service Slack.{{\/t}}<\/label>\n        <\/div>\n\n\n        <button id=\"submit_btn\" type=\"submit\" class=\"{{#unless email_reminded}}disabled{{\/unless}} btn btn_large ladda-button submit_setting\" data-style=\"expand-right\" data-qa=\"submit_button\">\n            <span class=\"ladda-label\">{{#t}}Suivant{{\/t}}<i class=\"ts_icon ts_icon_arrow_right\"><\/i><\/span>\n        <\/button>\n    <\/div>\n{{else}}\n<div id=\"submit\" class=\"fs_split_footer\">\n\n    <div id=\"email_checkbox\">\n        <input type=\"checkbox\" id=\"email_misc\" data-qa=\"email_checkbox\" {{#if uncheck_email}}{{else}}checked{{\/if}}>\n        <label for=\"email_misc\">{{#t}}Je suis d\u2019accord pour recevoir (tr\u00e8s occasionnellement)<br class=\"hide_on_mobile\" \/> des e-mails \u00e0 propos des services Slack.{{\/t}}<\/label>\n    <\/div>\n\n\n    <button id=\"submit_btn\" type=\"submit\" class=\"{{#unless email_reminded}}disabled{{\/unless}} btn btn_large ladda-button submit_setting\" data-style=\"expand-right\" data-qa=\"submit_button\">\n        <span class=\"ladda-label\">{{#t}}Suivant{{\/t}}<i class=\"ts_icon ts_icon_arrow_right\"><\/i><\/span>\n    <\/button>\n<\/div>\n{{\/if}}\n\n<p class=\"mobile_legalese fs_split_well show_on_mobile align_center\">\n    {{#t}}En poursuivant en vue de cr\u00e9er un compte et d'utiliser Slack, vous acceptez nos <a class=\"bold\" href=\"\/terms-of-service\">conditions d'utilisation<\/a> et nos <a href=\"\/privacy-policy\" class=\"bold\">r\u00e8gles de confidentialit\u00e9<\/a>. Si vous ne les acceptez pas, vous ne pouvez pas utiliser Slack.{{\/t}}\n<\/p>\n";
TS.raw_templates['invite_password_form'] = "{{i18n_ns \"invite\"}}\n<div class=\"fs_split_header\"><\/div>\n\n<div id=\"ratelimit_alert\" class=\"header_error_message hidden bottom_margin\">\n    <h3>{{#t note=\"This refers to a rate limit we have for filling out this form\"}}Houl\u00e0, vous allez trop vite\u00a0!{{\/t}}<\/h3>\n    <p class=\"no_margin\">{{#t}}Ralentissez un peu. Vous aurez peut-\u00eatre plus de r\u00e9ussite si vous r\u00e9essayez dans une minute.{{\/t}}<\/p>\n<\/div>\n\n<div id=\"invite_form_body\" class=\"fs_split_body\">\n     <h1>{{#t}}D\u00e9finir votre mot de passe{{\/t}}<\/h1>\n\n    <p class=\"subtle_silver\">{{#t}}Choisissez un mot de passe pour vous connecter \u00e0 {team_name} sur Slack.{{\/t}}<\/p>\n\n    <div id=\"password_container\">\n        <label class=\"normal\" for=\"password\">{{#t}}Mot de passe{{\/t}}<\/label>\n        <input type=\"password\" id=\"invite_password\" data-qa=\"password-field\" placeholder=\"{{#t}}Mot de passe{{\/t}}\" name=\"password\"\/>\n        <p class=\"error_message\" id=\"password_error_message\"><\/p>\n\n        <p class=\"subtle_silver\">{{#t}}Les mots de passe doivent contenir au moins 6\u00a0caract\u00e8res et ne doivent pas \u00eatre faciles \u00e0 deviner. \u00c9vitez par exemple \u00ab\u00a0mot de passe\u00a0\u00bb, \u00ab\u00a0123456\u00a0\u00bb ou \u00ab\u00a0abcdef\u00a0\u00bb.{{\/t}}<\/p>\n    <\/div>\n<\/div>\n\n<div id=\"submit\" class=\"fs_split_footer large_bottom_margin \">\n    <button id=\"submit_btn\" type=\"submit\" class=\"disabled btn btn_large ladda-button submit_setting\" data-style=\"expand-right\" data-qa=\"submit_button\">\n        <span class=\"ladda-label\">{{#t}}Suivant{{\/t}}<i class=\"ts_icon ts_icon_arrow_right\"><\/i><\/span>\n    <\/button>\n<\/div>\n";
