TS.raw_templates['bot_icon_modal'] = "{{i18n_ns 'bot_icon'}}\n<h1>{{#t}}Charger une ic\u00f4ne{{\/t}}<\/h1>\n<div class=\"alert bottom_margin hidden\" id=\"icon_upload_alert\"><i class=\"ts_icon ts_icon_warning\"><\/i><span class=\"alert_body\"><\/span><\/div>\n<div id=\"bot_icon_modal\" data-current-step=\"upload\">\n\t<div class=\"step\" data-progress=\"prompt\" data-step=\"upload\" id=\"upload_step\">\n\t\t<div class=\"drop_zone\">\n\t\t\t<span class=\"upload_prompt\">{{#t}}D\u00e9poser un fichier image ici ou cliquez pour le charger.{{\/t}}<\/span>\n\t\t\t<div class=\"upload_progress\">\n\t\t\t\t<div id=\"upload_progress_circle\"><\/div>\n\t\t\t\t<span>{{#t}}Chargement...{{\/t}}<\/span>\n\t\t\t<\/div>\n\t\t<\/div>\n\t\t<form>\n\t\t\t<input type=\"file\" id=\"hidden_file\" name=\"image\">\n\t\t<\/form>\n\t<\/div>\n\t<div class=\"step\" data-step=\"crop\">\n\t\t<img id=\"crop_img\">\n\t\t<div class=\"actions\">\n\t\t\t<button class=\"btn ladda-button disabled\" data-style=\"expand-right\" id=\"crop_btn\" disabled=\"disabled\"><span class=\"ladda-label\">{{#t}}Rogner{{\/t}}<\/span><\/button>\n\t\t<\/div>\n\t<\/div>\n<\/div>\n";
