TS.raw_templates['apps_typeahead'] = "{{i18n_ns \"app_directory\"}}\n<div class=\"apps_typeahead\">\n\t{{#if data.suggestions}}\n\t\t{{#each data.suggestions}}\n\t\t\t<a href=\"\/apps\/{{encoded_id}}\" class=\"plastic_typeahead_item\" data-app-id=\"{{encoded_id}}\" data-app-name=\"{{name}}\" data-app-is-slack-owned=\"{{is_slack_owned}}\" data-position={{math @index '+' 1}}>\n\t\t\t\t{{#if payload.icons.image_48}}\n\t\t\t\t\t<img src=\"{{payload.icons.image_48}}\" class=\"apps_typeahead_item_media\" \/>\n\t\t\t\t{{else}}\n\t\t\t\t\t<img src=\"{{versioned_default_application_icon}}\" class=\"apps_typeahead_item_media\" \/>\n\t\t\t\t{{\/if}}\n\t\t\t\t<div class=\"apps_typeahead_item_text\">\n\t\t\t\t\t<span class=\"apps_typeahead_item_title\">{{name}}<\/span>\n\t\t\t\t\t<span class=\"apps_typeahead_item_desc\">{{desc}}<\/span>\n\t\t\t\t<\/div>\n\t\t\t<\/a>\n\t\t{{\/each}}\n\t\t{{#if data.has_more_suggestions}}\n\t\t\t<a href=\"\/apps\/search?q={{term}}\" class=\"plastic_typeahead_item\">\n\t\t\t\t<ts-icon class=\"ts_icon_search apps_typeahead_item_media\"><\/ts-icon>\n\t\t\t\t<div>\n\t\t\t\t\t<span class=\"apps_typeahead_item_title\">{{#t note='Link at bottom of search typeahead to see more'}}Afficher tous les r\u00e9sultats pour \u00ab\u00a0{term}\u00a0\u00bb{{\/t}}<\/span>\n\t\t\t\t<\/div>\n\t\t\t<\/a>\n\t\t{{\/if}}\n\t{{else}}\n\t\t<div class=\"plastic_typeahead_item plastic_typeahead_item_no_results\">{{#t note='Message at bottom of search typeahead when no results of'}}Aucun r\u00e9sultat pour \"{term}\"{{\/t}}<\/div>\n\t{{\/if}}\n<\/div>\n";
TS.raw_templates['generic_dialog'] = "{{!\n\nNote: this template used to be inlined in TS.templates.source.js, and as a side effect of the way\nwe represented templates, all of the newlines were actually elided. So, that's what the crazy\nthings are around all of the line endings below. If you're refactoring this, future engineer,\nfeel free to take them out -- I just didn't want to change the meaning of the templates during\nmy refactoring. Love, Mark.\n\n}}<div class=\"modal-header c-modal_header--{{type}}\">{{!\n}}  {{#if img}}<span class=\"modal-header-image modal-header-image--{{img}}\"><\/span>{{\/if}}{{!\n}}\t<button type=\"button\" class=\"close c-modal_header__close--{{type}} ts_icon ts_icon_times\" data-dismiss=\"modal\" aria-hidden=\"true\"><\/button>{{!\n}}\t<h3>{{title}} <img src=\"{{versioned_loading_animation}}\" width=\"16\" height=\"16\" class=\"throbber hidden\"><\/h3>{{!\n}}<\/div>{{!\n}}<div class=\"modal-body c-modal_body--{{type}}\">{{!\n}}\t{{body}}{{!\n}}<\/div>{{!\n}}<div class=\"modal-footer c-modal_footer--{{type}}\">{{!\n}}\t{{#if footer_content}}{{!\n}}\t\t{{footer_content}}{{!\n}}  {{\/if}}{{!\n}}\t<button type=\"button\" class=\"btn btn_outline dialog_cancel c-modal_footer__cancel--{{type}}\" data-qa=\"generic_dialog_cancel\"><\/button>{{!\n}}\t<button type=\"button\" class=\"btn btn dialog_secondary_go hidden c-modal_footer__secondary_go--{{type}}\" data-qa=\"generic_dialog_secondary_go\"><\/button>{{!\n}}{{#if ladda}}{{!\n}}\t<button type=\"button\" class=\"btn dialog_go ladda-button c-modal_footer__go--{{type}}\" data-style=\"expand-right\" data-qa=\"generic_dialog_go\"><span class=\"ladda-label\"><\/span><\/button>{{!\n}}{{else}}{{!\n}}\t<button type=\"button\" class=\"btn dialog_go c-modal_footer__go--{{type}}\" data-qa=\"generic_dialog_go\"><\/button>{{!\n}}{{\/if}}{{!\n}}<\/div>\n";
