TS.raw_templates['calls_screen_share_controls'] = "{{i18n_ns \"calls\"}}\n\n<div class=\"ss_controls ss_controls--{{#if is_host }}host{{else}}guest{{\/if}}\">\n    <div class=\"ss_controls__info_container\">\n        <div class=\"ss_controls__speaker\">\n            <span class=\"ss_controls__empty_speaker_message\">{{#t}}En attente de personnes...{{\/t}}<\/span>\n            <img class=\"ss_controls__speaker--avatar\" \/>\n            <div class=\"ss_controls__speaker_info_container\">\n                <span class=\"ss_controls__speaker--desc\">{{#t note=\"shown above the user's name that is currently speaking\"}}Intervenant actuel{{\/t}}<\/span>\n                <span class=\"ss_controls__speaker--name\"><\/span>\n            <\/div>\n        <\/div>\n        <div class=\"ss_controls__reaction\">\n            <span class=\"ss_controls__reaction--emoji\"><\/span>\n            <span class=\"ss_controls__reaction--name\"><\/span>\n        <\/div>\n        <span class=\"ss_controls__help_text\">\n            {{!-- These translations need to live here because we don't have TS.i18n in the mini panel --}}\n            <span name=\"end_share\">{{#t}}Arr\u00eater de partager votre \u00e9cran{{\/t}}<\/span>\n            <span name=\"options\">{{#t}}Plus d\u2019options{{\/t}}<\/span>\n            <span name=\"mute_on\">{{#t}}D\u00e9sactiver le micro{{\/t}}<\/span>\n            <span name=\"mute_off\">{{#t}}Activer votre microphone{{\/t}}<\/span>\n            <span name=\"doodle_on\">{{#t}}Ne pas permettre aux autres utilisateurs de dessiner sur votre \u00e9cran{{\/t}}<\/span>\n            <span name=\"doodle_off\">{{#t}}Permettre aux autres utilisateurs de dessiner sur votre \u00e9cran{{\/t}}<\/span>\n            <span name=\"doodle_disabled_linux\">{{#t}}La fonctionnalit\u00e9 qui permet qui autres utilisateurs de dessiner sur votre \u00e9cran n'est pas prise en charge dans l'environnement Linux{{\/t}}<\/span>\n            <span name=\"interactive_on\">{{#t fallback_hash=e4