TS.raw_templates['admin_cant_deactivate_modal'] = "{{i18n_ns \"cant_deactivate_modal\"}}\n\n<span class=\"modal-header-image modal-header-image--oops\"><\/span>\n<h3>{{#t}}Impossible de retirer {full_member_name}{{\/t}}<\/h3>\n<p>{{#t group_list=(listify groups strong=true)}}{full_member_name} fait partie de {groups.length, plural, =1{# groupe}other{# groupes}} associ\u00e9(s) \u00e0 cette \u00e9quipe\u00a0: {group_list}.{{\/t}}<\/p>\n<p>{{#t}}Afin de retirer {member_name}, un administrateur de l\u2019organisation doit l'\u00f4ter des groupes dans {idp_label} ou soustraire les groupes de {team_name}.{{\/t}}<\/p>\n";
TS.raw_templates['admin_cancel_survey_dialog'] = "{{i18n_ns \"admin_billing_item\"}}\n\n<label class=\"normal p-cancel_survey__gray\">{{#t note='Prompt for users who just downgraded from a paid to unpaid plan'}}Si vous avez le temps, nous aimerions que vous nous disiez pourquoi vous avez choisi de passer au forfait gratuit.{{\/t}}<\/label>\n\n<form action=\"\" id=\"admin_cancel_survey\" name=\"admin_cancel_survey\">\n\t<fieldset class=\"c-radiogroup\">\n\t\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"radio-1\">\n\t\t\t<input type=\"radio\" name=\"cancel_survey\" value=\"switched\" class=\"c-input_radio\" id=\"radio-1\"\/>\n\t\t\t<span class=\"c-label__text normal lato\">{{#t}}Nous avons choisi une autre application de messagerie{{\/t}}<\/span>\n\t\t<\/label>\n\t\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"radio-2\">\n\t\t\t<input type=\"radio\" name=\"cancel_survey\" value=\"paid_features_useless\" class=\"c-input_radio\" id=\"radio-2\"\/>\n\t\t\t<span class=\"c-label__text normal lato\">{{#t}}Nous n\u2019avons pas utilis\u00e9 les fonctionnalit\u00e9s payantes{{\/t}}<\/span>\n\t\t<\/label>\n\t\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"radio-3\">\n\t\t\t<input type=\"radio\" name=\"cancel_survey\" value=\"expensive\" class=\"c-input_radio\" id=\"radio-3\"\/>\n\t\t\t<span class=\"c-label__text normal lato\">{{#t}}Le prix est trop \u00e9lev\u00e9{{\/t}}<\/span>\n\t\t<\/label>\n\t\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"radio-4\">\n\t\t\t<input type=\"radio\" name=\"cancel_survey\" value=\"did_not_use\" class=\"c-input_radio\" id=\"radio-4\"\/>\n\t\t<span class=\"c-label__text normal lato\">{{#t}}Notre utilisation de Slack \u00e9tait insuffisante{{\/t}}<\/span>\n\t\t<\/label>\n\t\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"radio-5\">\n\t\t\t<input type=\"radio\" name=\"cancel_survey\" value=\"no_longer_need\" class=\"c-input_radio\" id=\"radio-5\"\/>\n\t\t\t<span class=\"c-label__text normal lato\">{{#t}}Nous n\u2019avons plus besoin de Slack{{\/t}}&nbsp;<\/span>\n\t\t\t<span class=\"c-label__text normal lato p-cancel_survey__gray\">{{#t}}(par exemple, le projet est termin\u00e9, ou l\u2019entreprise a cess\u00e9 son activit\u00e9){{\/t}}<\/span>\n\t\t<\/label>\n\t\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"radio-6\">\n\t\t\t<input type=\"radio\" name=\"cancel_survey\" value=\"other\" class=\"c-input_radio\" id=\"radio-6\"\/>\n\t\t\t<span class=\"c-label__text normal lato\">{{#t}}Autre{{\/t}}<\/span>\n\t\t<\/label>\n\t<\/fieldset>\n\n\t<label class=\"c-label c-label--block\" for=\"cancel_survey_textarea\">\n\t\t<textarea class=\"c-input_textarea\" id=\"cancel_survey_textarea\" maxlength=\"500\" placeholder=\"{{#t note='Placeholder text for a form asking why users downgraded from a paid to unpaid plan'}}Ajouter un commentaire...{{\/t}}\"><\/textarea>\n\t<\/label>\n<\/form>\n";
TS.raw_templates['message_retention_warnings'] = "{{i18n_ns 'web_admin'}}\n\n<p class=\"data_retention_warning--long c-alert c-alert--multiline c-alert--boxed c-alert--level_warning display_none\">\n\t<i class=\"c-alert__icon c-deprecated-icon c-icon--warning c-deprecated-icon--vertical-align-baseline\"><\/i>\n\t<span class=\"c-alert__message\">{{#t}}<strong>Pour information<\/strong>, si vous souhaitez conserver ind\u00e9finiment tous vos messages, vous pouvez choisir <strong>Tout conserver<\/strong> dans le menu d\u00e9roulant ci-dessus.{{\/t}}\n\t<\/span>\n<\/p>\n\n<p class=\"data_retention_warning--short c-alert c-alert--multiline c-alert--boxed c-alert--level_warning display_none\">\n\t<i class=\"c-alert__icon c-deprecated-icon c-icon--warning c-deprecated-icon--vertical-align-baseline\"><\/i>\n\t<span class=\"c-alert__message\">{{#t note=\"data_retention_warning_value will be populated by code with the input duration, i.e. '100 days'\"}}<strong>Cela supprimera les messages qui ont plus de\u00a0: <span class=\"data_retention_warning_value\"><\/span><\/strong>. Nous vous recommandons de consulter votre \u00e9quipe avant de fixer une p\u00e9riode de conservation aussi courte.<\/span>{{\/t}}\n<\/p>\n\n<p class=\"data_retention_warning--not_valid c-alert c-alert--multiline c-alert--boxed c-alert--level_warning display_none\">\n\t<i class=\"c-alert__icon c-deprecated-icon c-icon--warning c-deprecated-icon--vertical-align-baseline\"><\/i>\n\t<span class=\"c-alert__message\">{{#t}}Pour supprimer les messages apr\u00e8s une certaine p\u00e9riode de temps, d\u00e9finissez une valeur sup\u00e9rieure \u00e0 z\u00e9ro.<\/span>{{\/t}}\n<\/p>\n\n<p class=\"data_retention_warning--max c-alert c-alert--multiline c-alert--boxed c-alert--level_warning display_none\">\n\t<i class=\"c-alert__icon c-deprecated-icon c-icon--warning c-deprecated-icon--vertical-align-baseline\"><\/i>\n\t<span class=\"c-alert__message\">{{#t}}Lorsque vous d\u00e9finissez une p\u00e9riode au bout de laquelle vos messages seront supprim\u00e9s, nous vous demandons de choisir une dur\u00e9e inf\u00e9rieure \u00e0 100\u00a0ans.<\/span>{{\/t}}\n<\/p>\n";
TS.raw_templates['message_retention_dialog'] = "{{i18n_ns 'web_admin'}}\n\n{{#if show_warning}}\n\t<p class=\"c-alert c-alert--boxed c-alert--level_error\">\n\t\t<i class=\"c-alert__icon c-deprecated-icon c-icon--warning c-deprecated-icon--vertical-align-baseline\"><\/i>\n\t\t<span class=\"c-alert__message\">{{#t}}<strong>Attention\u00a0:<\/strong> la suppression des messages, dans le cas o\u00f9 elle est provoqu\u00e9e par l\u2019ex\u00e9cution des param\u00e8tres de conservation, est d\u00e9finitive. Les messages ne pourront alors \u00eatre ni restaur\u00e9s, ni r\u00e9cup\u00e9r\u00e9s (m\u00eame par Slack).{{\/t}}<\/span>\n\t<\/p>\n{{\/if}}\n<strong>{{#t}}Voici vos nouveaux param\u00e8tres\u00a0:{{\/t}}<\/strong>\n<ul class=\"margin_top_100\">\n\t<li>{{#t note=\"The channel retention setting that was selected for public channels, i.e. Public channels - Keep messages, but don't track revisions\"}}Cha\u00eenes publiques \u2013 {public_channel_retention_setting}{{\/t}}<\/li>\n\t<li>{{#t note=\"The channel retention setting that was selected for private channels, i.e. Private channels - Keep messages, but don't track revisions\"}}Cha\u00eenes priv\u00e9es \u2013 {private_channel_retention_setting}{{\/t}}<\/li>\n\t{{#pageNeedsEnterprise}}\n\t{{else}}\n\t\t<li>{{#t note=\"The channel retention setting that was selected for direct messages, i.e. Direct messages - Keep messages, but don't track revisions\"}}Messages directs \u2013 {dm_retention_setting}{{\/t}}<\/li>\n\t{{\/pageNeedsEnterprise}}\n\t<li>\n\t\t{{#if allow_override}}\n\t\t\t{{#t}}Les membres de l\u2019espace de travail peuvent modifier ces param\u00e8tres{{\/t}}\n\t\t{{else}}\n\t\t\t{{#t}}Les membres de l\u2019espace de travail ne peuvent pas modifier ces param\u00e8tres{{\/t}}\n\t\t{{\/if}}\n\t<\/li>\n<\/ul>\n";
TS.raw_templates['message_retention_dialog_footer'] = "{{i18n_ns 'web_admin'}}\n\n<label class=\"margin_bottom_100 c-label c-label--inline c-label--pointer\" for=\"data-retention-confirm\">\n\t<input type=\"checkbox\" class=\"c-input_checkbox\" id=\"data-retention-confirm\" \/>\n\t<span class=\"c-label__text\"><strong>{{#t}}Oui, appliquer ces nouveaux param\u00e8tres{{\/t}}<\/strong><\/span>\n<\/label>\n";
TS.raw_templates['api_app_edit_bots_modal'] = "{{i18n_ns \"api\"}}\n\n{{#t}}Voulez-vous vraiment supprimer cet utilisateur bot de votre application\u00a0?{{\/t}}\n<div class=\"alert alert_error top_margin no_bottom_margin hidden\">\n\t<i class=\"ts_icon ts_icon_warning\"><\/i>\n\t{{#t}}D\u00e9sol\u00e9, nous n'avons pas pu retirer l'utilisateur bot. Ce n'est pas \u00e0 cause de vous, mais de nous. Nous allons r\u00e9gler ce probl\u00e8me.{{\/t}}\n<\/div>\n";
