TS.raw_templates['developer_apps_event_table'] = "{{i18n_ns \"developer_apps\"}}\n<div class=\"{{type}}_events_table_container\">\n\t<table id=\"{{type}}_events_table\" class=\"events_table full_width no_bottom_border no_bottom_margin {{#unless events}} hidden{{\/unless}}\">\n\t\t<thead>\n\t\t\t<tr>\n\t\t\t\t<th class=\"no_bottom_margin no_border\">\n\t\t\t\t\t{{#t note=\"heading descriptions for Events API events\"}}Nom de l'\u00e9v\u00e9nement{{\/t}}\n\t\t\t\t<\/th>\n\t\t\t\t<th class=\"no_bottom_margin no_border\">\n\t\t\t\t\t{{#t note=\"heading descriptions for Events API events\"}}Description{{\/t}}\n\t\t\t\t<\/th>\n\t\t\t\t{{#unless is_bot}}\n\t\t\t\t\t<th class=\"no_bottom_margin no_border\">\n\t\t\t\t\t\t{{#t note=\"heading descriptions for Events API events\"}}La port\u00e9e est requise{{\/t}}\n\t\t\t\t\t<\/th>\n\t\t\t\t{{\/unless}}\n\t\t\t<\/tr>\n\t\t<\/thead>\n\t\t\t{{#each events as |event|}}\n\t\t\t<tbody>\n\t\t\t\t\t<tr class=\"no_padding\"\n\t\t\t\t\t\t{{#unless event.current}}data-pending{{\/unless}}\n\t\t\t\t\t\tdata-event-type=\"{{@key}}\" data-event-scope=\"{{event.scope}}\"\n\t\t\t\t\t\tdata-event-description=\"{{event.description}}\" data-table=\"{{..\/type}}\"\n\t\t\t\t\t>\n\t\t\t\t\t\t<td class=\"no_bottom_padding\">\n\t\t\t\t\t\t\t<a class=\"overflow_ellipsis small {{#if event.pending_removal}}pending_removal{{\/if}} {{#if event.current}}current_{{else}}pending_{{\/if}}event_subscription_name\"\n\t\t\t\t\t\t\t\thref=\"\/events\/{{@key}}\" target=\"_blank\" data-is-pending-removal=\"{{#if pending_removal}}true{{else}}false{{\/if}}\">\n\t\t\t\t\t\t\t\t{{@key}}\n\t\t\t\t\t\t\t<\/a>\n\t\t\t\t\t\t<\/td>\n\t\t\t\t\t\t<td class=\"no_bottom_padding medium_right_padding {{#if event.pending_removal}}pending_removal{{\/if}}\">\n\t\t\t\t\t\t\t<p class=\"event_description small\">{{event.description}}<\/p>\n\t\t\t\t\t\t<\/td>\n\t\t\t\t\t\t{{#unless ..\/is_bot}}\n\t\t\t\t\t\t\t<td class=\"no_bottom_padding {{#if event.pending_removal}}pending_removal{{\/if}}\">\n\t\t\t\t\t\t\t\t<p class=\"event_scope small\">{{#if event.scope}}{{event.scope}}{{else}}<span class=\"subtle_silver\">{{#t note=\"text for no scopes needed\"}}aucun{{\/t}}<\/span>{{\/if}}<\/p>\n\t\t\t\t\t\t\t<\/td>\n\t\t\t\t\t\t{{\/unless}}\n\t\t\t\t\t\t<td class=\"no_bottom_padding\">\n\t\t\t\t\t\t\t{{!-- We set the flex direction to row_reverse so that if no description exists, the icons still appear on the right hand side  --}}\n\t\t\t\t\t\t\t<div class=\"display_flex flex_direction_row_reverse no_wrap\">\n\t\t\t\t\t\t\t\t\t<ts_icon class=\"{{#if event.current}}delete_event{{else}}remove_event{{\/if}} {{#if event.pending_removal}}ts_icon_undo{{else}}ts_icon_trash{{\/if}} btn_icon ts_icon cursor_pointer\"\n\t\t\t\t\t\t\t\t\t\taria-label=\"{{#t}}Supprimer{{\/t}}\">\n\t\t\t\t\t\t\t\t\t<\/ts_icon>\n\t\t\t\t\t\t\t<\/div>\n\t\t\t\t\t\t<\/td>\n\t\t\t\t\t<\/tr>\n\t\t\t\t<\/tbody>\n\t\t\t{{else}}\n\t\t\t\t<tbody class=\"no_border\">\n\t\t\t\t\t<tr class=\"no_bottom_border\">\n\t\t\t\t\t\t<td class=\"subtle_silver small\">{{#t note=\"no Events API events added yet\"}}Aucun \u00e9v\u00e9nement n'a encore \u00e9t\u00e9 ajout\u00e9.{{\/t}}<\/td>\n\t\t\t\t\t<\/tr>\n\t\t\t\t<\/tbody>\n\t\t\t{{\/each}}\n\t<\/table>\n<\/div>\n";
TS.raw_templates['developer_apps_new_modal'] = "{{i18n_ns \"developer_apps\"}}\n{{!-- Possible Error --}}\n<p class=\"alert alert_error hidden\" id=\"developer_apps_new_error\">\n\t<ts-icon class=\"ts_icon ts_icon_warning\"><\/ts-icon>\n\t<span class=\"error_message\">{{#t note=\"Generic error message when something goes wrong when a third party developer fills in a form to create an app\"}}Oups, une erreur s'est produite. Voulez-vous r\u00e9essayer\u00a0?{{\/t}}<\/span>\n<\/p>\n\n<form action=\"\/apps\/create_new\" enctype=\"multipart\/form-data\" method=\"post\" id=\"developer_apps_new_form\" class=\"p-new_app_creation__form\">\n\t<input type=\"hidden\" name=\"done\" value=\"1\">\n\t<input type=\"hidden\" name=\"crumb\" value=\"{{crumb}}\">\n\n\t{{#if_equal token_type compare=\"xoxp\"}}\n\t<a href=\"\/slack-apps-preview\" target=\"_blank\" class=\"p-app_creation_banner__anchor\">\n\t\t<div class=\"p-app_creation_banner {{#if env_is_dev}}p-app_creation_banner--is-dev{{\/if}}\">\n\n\t\t\t{{> developer_apps_new_modal_flask_svg}}\n\n\t\t\t<div class=\"p-app_creation_banner__content\">\n\t\t\t\t<h2 class=\"p-app_creation_banner__header\">{{#t note=\"Title text of a call to action to a blog post\"}}Les applications nouvelle g\u00e9n\u00e9ration vous int\u00e9ressent\u00a0?{{\/t}}<\/h2>\n\t\t\t\t<p class=\"p-app_creation_banner__para\">{{#t note=\"call to action text\"}}Nous nous effor\u00e7ons d\u2019am\u00e9liorer le d\u00e9veloppement et la distribution des applications. Rejoignez la p\u00e9riode Aper\u00e7u API pour les jetons d\u2019espace de travail et l\u2019API Autorisations.{{\/t}}<\/p>\n\t\t\t<\/div>\n\t\t<\/div>\n\t<\/a>\n\t{{\/if_equal}}\n\n\t{{!-- App Name --}}\n\t<label for=\"name\">{{#t note=\"Title of the form which shows up in a modal when a third party developer wishes to create an app\"}}Nom de l'application{{\/t}}<\/label>\n\t<input type=\"text\" name=\"name\" placeholder=\"{{#t note=\"Placeholder text for a name of an app\"}}par exemple, Super service{{\/t}}\" class=\"no_bottom_margin overflow_ellipsis\" value=\"\" autocomplete=\"off\"\n\t\tdata-validation=\"required keywords maxlength=35\">\n\t<aside class=\"p-new_app_creation__aside medium_bottom_margin\">{{#t note=\"Helper text reassuring a third party developer that the name they chose for their app can be changed at a later date\"}}Ne vous inqui\u00e9tez pas\u00a0; vous serez en mesure de modifier cela plus tard.{{\/t}}<\/aside>\n\n\t{{!-- Team Selector --}}\n\t<label for=\"team_id\">{{#t note=\"Label for a dropdown where a third party developer chooses Slack Team their proposed app will belong to\"}}Espace de travail d\u00e9veloppement Slack{{\/t}}<\/label>\n\t<div id=\"team_id_picker\"><\/div>\n\t{{!-- Hidden input for id of team selected, since the team selector is actually a menu --}}\n\t<input name=\"team_id\" class=\"hidden\" value=\"\" data-validation=\"required\" \/>\n\t<aside class=\"p-new_app_creation__aside medium_bottom_margin\">\n\t\t{{#t note=\"helper text letting the third party developer know what happens to the app they're creating if they leave their team\"}}Votre application appartient \u00e0 cet espace de travail. Si vous quittez cet espace de travail, vous ne pourrez plus la g\u00e9rer. Notez que ce choix est d\u00e9finitif.{{\/t}}\n\t<\/aside>\n\n\t<input type=\"hidden\" name=\"app_type\" value=\"{{token_type}}\">\n\n\t{{!-- ToS Agreement --}}\n\t<div class=\"small\">{{#t note=\"Legal copy targetted towards a third party developer creating an app using our api\"}}En cr\u00e9ant une application API web, vous acceptez les <a target=\"_blank\" href=\"\/terms-of-service\/api\">Conditions d'utilisation des API Slack<\/a>.{{\/t}}<\/div>\n<\/form>\n";
