TS.raw_templates['developer_apps_url_verification'] = "{{i18n_ns \"developer_apps\"}}\n\n<div data-js=\"request_url_verification_container\" data-status={{status}}>\n\t{{!-- Request URL Labels --}}\n\t<label for=\"{{#if changing_url}}previous_{{\/if}}request_url\" class=\"block align_left current_request_url\">\n\t\t{{#if changing_url}}\n\t\t\t{{#t note=\"This is a field where we compare an already saved URL with one we wish to replace\"}}URL de la requ\u00eate pr\u00e9c\u00e9dente{{\/t}}\n\t\t{{else}}\n\t\t\t{{#t note=\"This is a field where we wish to add a new URL to send slash command requests to\"}}URL de la requ\u00eate{{\/t}}\n\t\t{{\/if}}\n\t\t<span class=\"verification_dialogue_container inline {{#if verified}}verified{{\/if}}\">\n\t\t{{#or unverified first_verification}}\n\t\t\t<p class=\"verification_help_text small inline {{#if failed_verification}}warning_text{{else if verified}}verified{{\/if}} small_left_margin\">\n\t\t\t\t{{#if failed_verification}}\n\t\t\t\t\t{{#t note=\"This is an error we respond with when the developer's server did not respond with the value of the challenge parameter\"}}Votre URL n'a pas r\u00e9pondu avec la valeur du param\u00e8tre de <code>challenge<\/code>.{{\/t}}\n\t\t\t\t{{else if verified}}\n\t\t\t\t\t{{#t note=\"Verified means the developer's server responded properly\"}}V\u00e9rifi\u00e9{{\/t}}\n\t\t\t\t\t<ts-icon class=\"ts_icon_check_small verified\"><\/ts-icon>\n\t\t\t\t{{\/if}}\n\t\t\t<\/p>\n\t\t\t<div class=\"verification_spinner inline hidden\">\n\t\t\t\t{{> loading_indicator}}\n\t\t\t<\/div>\n\t\t{{else}}\n\t\t\t{{#unless skip_verification_notification}}\n\t\t\t\t{{#t note=\"Verified means the developer's server responded properly\"}}V\u00e9rifi\u00e9{{\/t}}\n\t\t\t\t<ts-icon class=\"ts_icon_check_small verified\"><\/ts-icon>\n\t\t\t{{\/unless}}\n\t\t{{\/or}}\n\t\t<\/span>\n\t<\/label>\n\n\t{{!-- Request URL Input Field(s) --}}\n\t<input type=\"text\" name=\"{{#if changing_url}}previous_{{\/if}}request_url\" id=\"{{#if changing_url}}previous_{{\/if}}request_url\"\n\t\tclass=\"small no_bottom_margin\n\t\t\t{{#unless changing_url}}\n\t\t\t\trequest_url\n\t\t\t{{else}}\n\t\t\t\tprevious_request_url\n\t\t\t{{\/unless}}\n\t\t\t{{#if failed_verification}}\n\t\t\t\tfailed_verification\n\t\t\t{{\/if}}\n\t\t\"\n\t\tplaceholder=\"{{#t note=\"example url endpoint\"}}https:\/\/my.app.com\/slack\/action-endpoint{{\/t}}\"\n\t\t{{#or unverified first_verification}}\n\t\t\tdata-validation=\"isurl=http{{#unless allow_non_https}}s{{\/unless}}\" data-validation-success=\" \"\n\t\t\t{{#if allow_non_https}}\n\t\t\t\tdata-validation-error=\"{{#t}}Uniquement des URL valides, merci\u00a0!{{\/t}}\"\n\t\t\t{{else}}\n\t\t\t\tdata-validation-error=\"{{#t}}URLs de type HTTPS valides uniquement, merci\u00a0!{{\/t}}\"\n\t\t\t{{\/if}}\n\t\t{{else}}\n\t\t\tdisabled\n\t\t{{\/or}}\n\t\tvalue=\"{{#if prev_url}}{{prev_url}}{{else if url}}{{url}}{{\/if}}\"\n\t\tdata-js=\"first-url-field\"\n\t>\n\n\t{{#or failed_verification verified}}\n\t\t<div class=\"inline_textfield_button {{#if changing_url}}hidden{{\/if}}\">\n\t\t\t<button\n\t\t\t\tid=\n\t\t\t\t\t{{#if failed_verification}}\n\t\t\t\t\t\t\"retry_url_verification\"\n\t\t\t\t\t{{else}}\n\t\t\t\t\t\t\"change_request_url\"\n\t\t\t\t\t{{\/if}}\n\t\t\t\tclass=\"btn btn_small btn_outline small_top_margin\" for=\"request_url\">\n\t\t\t\t{{#if failed_verification}}\n\t\t\t\t\t{{#t note=\"Button for the developer to retry verifying their URL\"}}R\u00e9essayer{{\/t}}\n\t\t\t\t{{else}}\n\t\t\t\t\t{{#t note=\"Button for the developer to change their URL\"}}Changer{{\/t}}\n\t\t\t\t{{\/if}}\n\t\t\t<\/button>\n\t\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0<\/div>\n\t{{\/or}}\n\t{{#if changing_url}}\n\t\t{{!-- Label for New Request URL --}}\n\t\t<label for=\"new_request_url\" class=\"block align_left\">{{#t note=\"a Request URL is a URL a third party developer owns that Slack sends requests to\"}}URL de requ\u00eate{{\/t}}\n\t\t\t<span class=\"verification_dialogue_container inline\">\n\t\t\t\t<p class=\"verification_help_text inline small small_left_margin\n\t\t\t\t\t{{#if new_url_verified}}\n\t\t\t\t\t\tverified\n\t\t\t\t\t{{else if failed_verification}}\n\t\t\t\t\t\twarning_text\n\t\t\t\t\t{{\/if}}\n\t\t\t\t\">\n\t\t\t\t{{#if new_url_verified}}\n\t\t\t\t\t{{#t note=\"Verified means the developer's server responded properly\"}}V\u00e9rifi\u00e9{{\/t}}\n\t\t\t\t\t<ts-icon class=\"ts_icon_check_small verified\"><\/ts-icon>\n\t\t\t\t{{else if failed_verification}}\n\t\t\t\t\t{{#t note=\"This is an error we respond with when the developer's server did not respond with the value of the challenge parameter\"}}Votre URL n'a pas r\u00e9pondu avec la valeur du param\u00e8tre de <code>challenge<\/code>.{{\/t}}\n\t\t\t\t{{\/if}}\n\t\t\t\t<\/p>\n\t\t\t\t<div class=\"verification_spinner inline hidden\">\n\t\t\t\t\t{{> loading_indicator}}\n\t\t\t\t<\/div>\n\t\t\t<\/span>\n\t\t<\/label>\n\t\t{{!-- Input Field for New Request URL --}}\n\t\t<input type=\"text\" name=\"new_request_url\" id=\"request_url\" class=\"small no_bottom_margin new_request_url request_url verified\"\n\t\t\tdata-qa=\"event_request_url\"\n\t\t\tplaceholder=\"{{#t note=\"example url endpoint\"}}https:\/\/my.app.com\/slack\/action-endpoint{{\/t}}\"\n\t\t\tdata-validation=\"isurl=http{{#unless allow_non_https}}s{{\/unless}}\"\n\t\t\tdata-validation-success=\" \"\n\t\t\t{{#if allow_non_https}}\n\t\t\t\tdata-validation-warning=\"{{#t}}Uniquement des URL valides, merci\u00a0!{{\/t}}\"\n\t\t\t{{else}}\n\t\t\t\tdata-validation-warning=\"{{#t}}URLs de type HTTPS valides uniquement, merci\u00a0!{{\/t}}\"\n\t\t\t{{\/if}}\n\t\t\tvalue=\"{{new_url}}\">\n\t\t<div class=\"inline_textfield_button\">\n\t\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0<button id=\"cancel_change_url\" class=\"btn btn_small btn_outline small_top_margin\" for=\"request_url\">{{#t note=\"cancel button\"}}Annuler{{\/t}}<\/button>\n\t\t{{#if failed_verification}}\n\t\t\t<button id=\"retry_url_verification\" class=\"btn btn_small btn_outline small_top_margin\" for=\"new_request_url\">{{#t note=\"retry button\"}}R\u00e9essayer{{\/t}}<\/button>\n\t\t{{\/if}}\n\t\t<\/div>\n\t{{\/if}}\n\t{{#if response}}\n\t\t<div class=\"failed_url_verification_challenge\">\n\t\t{{!-- hbslint-disable unwrapped --}}\n\t\t{{!-- Everything that needs to be wrapped already is. --}}\n\t\t<pre><code><span class=\"bold\">{{#t note=\"Title of a block of code showing our request data\"}}Notre demande\u00a0:{{\/t}}<\/span><br>POST<br>\"body\": { <br>&#09 \"type\": <span class=\"code_string request\">\"url_verification\"<\/span>,<br>&#09 \"token\": <span class=\"code_string request\">\"{{response.token}}\"<\/span>,<br>&#09 \"challenge\": <span class=\"code_string request\">\"{{response.challenge}}\"<\/span><br>}<br><span class=\"bold\">{{#t note=\"Title of a block of code showing the developer's response\"}}Votre r\u00e9ponse\u00a0:{{\/t}}<\/span><br>\"code\": <span class=\"code_string response\">{{response.code}}<\/span><br>\"error\": <span class=\"code_string response\">\"{{response.error}}\"<\/span><br>\"body\": {<br> <span class=\"code_string response\">{{response.body}}<\/span> <br>}<\/code><\/pre>\n\t\t{{!-- hbslint-enable unwrapped --}}\n\t\t<\/div>\n\t{{\/if}}\n\t<aside class=\"small subtle_silver top_margin bottom_margin\">{{#t}}Nous enverrons des requ\u00eates HTTP POST \u00e0 cette URL lorsque des \u00e9v\u00e9nements auront lieu. D\u00e8s que vous saisirez une URL, nous vous enverrons une requ\u00eate avec un param\u00e8tre<code>challenge<\/code>, et votre point de terminaison doit r\u00e9pondre avec la valeur de challenge.<a href=\"\/events\/url_verification\"> Plus d'informations.<\/a>{{\/t}}<\/aside>\n<\/div>\n";
TS.raw_templates['developer_apps_url_table_editor'] = "{{i18n_ns \"developer_apps\"}}\n<div data-js=\"url-table-editor\">\n\n\t<div data-js=\"url-table-editor-alert\" class=\"alert alert_error hidden\">\n\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t<span class=\"message\">{{#t}}Oups, une erreur s'est produite{{\/t}}<\/span>\n\t<\/div>\n\t<h2 data-js=\"url-table-editor-header\" class=\"c-url_table_editor__title\">{{ options.title }}<\/h2>\n\t<div data-js=\"url-table-editor-body\" class=\"small_bottom_margin\">\n\t\t{{#if options.show_empty_message}}\n\t\t\t<div data-js=\"url-table-editor-empty-message-container\"><\/div>\n\t\t{{\/if}}\n\t\t{{#if model}}\n\t\t\t{{#each model}}\n\t\t\t\t{{> developer_apps_url_table_editor_row index=@key is_read_mode=true cancel_button_text=..\/options.cancel_button_text save_button_text=..\/options.save_button_text edit_button_text=..\/options.edit_button_text validation=..\/options.validation id=..\/options.id placeholder=..\/options.placeholder }}\n\t\t\t{{\/each}}\n\t\t{{\/if}}\n\t<\/div>\n\n\t<div data-js=\"url-table-editor-footer\">\n\t\t{{#if options.footer}}\n\t\t\t<aside class=\"small subtle_silver bottom_margin\">{{options.footer}}<\/aside>\n\t\t{{\/if}}\n\n\t\t<button data-js-url-editor=\"create\" class=\"btn_outline btn block bottom_margin\" data-qa=\"add_domain_button\">{{ options.add_button_text }}<\/button>\n\t\t{{#if promise_to_save_button}}\n\t\t\t<button data-js-url-editor=\"save-all\" class=\"btn ladda-button block small_bottom_margin c-url_table_editor__save_all\" data-spinner-size=\"32\" data-style=\"expand-right\">{{ options.save_all_urls_text }}<\/button>\n\t\t{{\/if}}\n\t<\/div>\n<\/div>";
TS.raw_templates['developer_apps_url_table_editor_empty_message'] = "<div data-js=\"url-table-editor-empty-message\" class=\"top_margin bottom_margin\">\n\t<p class=\"subtle_silver align_center no_margin\">{{ message }}<\/p>\n<\/div>";
TS.raw_templates['developer_apps_url_table_editor_row'] = "<div data-row=\"{{ index }}\" class=\"clearfix very_small_bottom_padding small_top_padding c-url_table_editor__container\">\n\t<div class=\"float_left c-url_table_editor__fields\">\n\t\t{{#if is_delete_mode}}\n\t\t\t<input type=\"url\" name=\"urls[{{ index }}]{{#if id}}_{{id}}{{\/if}}\" class=\"c-url_table_editor__deleted_state small no_margin c-url_table_editor__input\" data-url-table-editor-row value=\"{{ url }} was deleted\" disabled>\n\t\t{{else}}\n\t\t\t<input type=\"url\" name=\"urls[{{ index }}]{{#if id}}_{{id}}{{\/if}}\" class=\"small no_margin c-url_table_editor__input\" data-qa=\"app_unfurl_domain\" value=\"{{ url }}\" {{#if is_read_mode}}disabled{{\/if}} data-url-table-editor-input data-validation=\"{{ validation }}\"{{#if placeholder}} placeholder=\"{{placeholder}}\"{{\/if}}>\n\t\t\t<label for=\"urls[{{ index }}]{{#if id}}_{{id}}{{\/if}}\" class=\"no_margin\"><\/label>\n\t\t\t<span class=\"validation_message\"><\/span>\n\t\t\t{{#feature flag=\"feature_token_ip_whitelist\"}}\n\t\t\t\t<span class=\"subtle_silver c-url_table_editor__annotation\" data-annotation-row=\"{{index}}\">{{#if annotation}}{{annotation}}{{\/if}}<\/span>\n\t\t\t{{\/feature}}\n\t\t{{\/if}}\n\t<\/div>\n\t<div class=\"float_left c-url_table_editor__actions\">\n\t\t{{#if is_create_mode}}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"cancel-create\" button_class=\"btn btn_outline small_left_margin float_left\" button_text=cancel_button_text }}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"save-create\" button_class=\"btn small_left_margin float_left\" data-qa=\"activate_enable_events_toggle\" button_text=save_button_text }}\n\t\t{{\/if}}\n\t\t{{#if is_read_mode}}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"edit\" button_class=\"btn_icon btn_outline btn c-url_table_editor__buttons ts_icon ts_icon_pencil\" }}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"delete\" button_class=\"btn_icon btn_outline btn c-url_table_editor__buttons ts_icon ts_icon_trash\" }}\n\t\t{{\/if}}\n\t\t{{#if is_edit_mode}}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"cancel-update\" button_class=\"btn btn_outline small_left_margin float_left\" button_text=cancel_button_text }}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"save-update\" button_class=\"btn small_left_margin float_left\" data-qa=\"activate_enable_events_toggle\" button_text=edit_button_text }}\n\t\t{{\/if}}\n\t\t{{#if is_delete_mode}}\n\t\t\t{{> developer_apps_url_table_editor_button action=\"restore\" button_class=\"btn_icon c-url_table_editor__buttons btn_outline btn ts_icon ts_icon_undo\"}}\n\t\t{{\/if}}\n\t<\/div>\n<\/div>";
