TS.raw_templates['gdrive_coachmark'] = "{{i18n_ns 'google_drive'}}\n\n<div id=\"gdrive_coachmark_div\" class=\"coachmark_div\">\n\t<div class=\"coachmark_contents\">\n\t\t\t<h2 class=\"black\">{{#t}}Google Drive, d\u00e9sormais en un seul clic{{\/t}}<\/h2>\n\t\t\t<p>\n\t\t\t\t{{#t}}Importez les fichiers Google Drive existants \u00e0 l\u2019aide de ce bouton pratique. Vous pouvez \u00e9galement cr\u00e9er de nouveaux fichiers Google Docs, Slides et Sheets sans quitter Slack.{{\/t}}\n\t\t\t<\/p>\n\t<\/div>\n<\/div>";
TS.raw_templates['developer_apps_permission_scopes_table'] = "{{i18n_ns \"developer_apps\"}}\n{{#each groups}}\n\t<table class=\"full_width no_bottom_margin{{#if ..\/show_reasons}} permission_scopes_group_table{{\/if}}\">\n\t\t<thead>\n\t\t\t<tr class=\"off_white_bg tiny permission_scopes_table_row\">\n\t\t\t\t<th class=\"small_left_padding very_small_top_padding very_small_bottom_padding no_border\">{{this.group_name}}<\/th>\n\t\t\t\t<th class=\"very_small_top_padding very_small_bottom_padding no_border\">{{#if ..\/show_reasons}}{{#t note=\"Heading in table column where user inputs their reason for using a scope\"}}RAISON D'UTILISATION DE LA PORT\u00c9E{{\/t}}{{\/if}}<\/th>\n\t\t\t<\/tr>\n\t\t<\/thead>\n\t\t<tbody>\n\t\t\t{{#each this.children}}\n\t\t\t\t<tr class=\"permission_scopes_table_row\">\n\t\t\t\t\t<td class=\"small_left_padding top_padding bottom_padding\">\n\t\t\t\t\t\t<p class=\"bold no_margin\">{{this.description}}<\/p>\n\t\t\t\t\t\t<span class=\"subtle_silver\">\n\t\t\t\t\t\t\t{{this.name}}\n\t\t\t\t\t\t<\/span>\n\t\t\t\t\t<\/td>\n\t\t\t\t\t<td class=\"top_padding bottom_padding no_right_padding display_flex align_items_center{{#if ..\/..\/show_reasons}}{{else}} justify_content_end{{\/if}}\">\n\t\t\t\t\t\t{{#if ..\/..\/show_reasons}}<textarea rows=\"1\" data-scope-reason=\"{{this.name}}\" class=\"no_bottom_margin small_padding permission_scopes_table_reason\" placeholder=\"{{#t note='Placeholder text where user input reason for using scope'}}Motif d'utilisation de la port\u00e9e...{{\/t}}\">{{this.reason}}<\/textarea>{{\/if}}\n\t\t\t\t\t\t<ts-icon{{#unless this.required}} data-remove-scope=\"{{this.name}}\"{{\/unless}} class=\"ts_icon_trash cursor_pointer left_margin{{#if this.required}} subtle_silver ts_tip ts_tip_top ts_tip_multiline ts_tip_float{{\/if}}{{#if ..\/..\/show_reasons}}{{else}} small_top_margin{{\/if}}\">\n\t\t\t\t\t\t\t{{#if this.required}}\n\t\t\t\t\t\t\t\t<span class=\"ts_tip_tip\">\n\t\t\t\t\t\t\t\t\t<span class=\"ts_tip_multiline_inner\">{{#t note=\"Text in tooltip for a required scope\"}}Cette port\u00e9e est n\u00e9cessaire pour la fonctionnalit\u00e9 de votre application et ne peut pas \u00eatre supprim\u00e9e.{{\/t}}<\/span>\n\t\t\t\t\t\t\t\t<\/span>\n\t\t\t\t\t\t\t{{\/if}}\n\t\t\t\t\t\t<\/ts-icon>\n\t\t\t\t\t<\/td>\n\t\t\t\t<\/tr>\n\t\t\t{{\/each}}\n\t\t<\/tbody>\n\t<\/table>\n{{\/each}}\n";
TS.raw_templates['developer_apps_permission_scopes_lfs_item'] = "{{i18n_ns \"developer_apps\"}}\n\n<span class=\"permission_scopes_filter_select_description float_left overflow_ellipsis inline_block{{#if chosen}} subtle_silver{{\/if}}\">{{description}}{{#if chosen}} {{#t}}(d\u00e9j\u00e0 ajout\u00e9){{\/t}}{{\/if}}<\/span>\n<span class=\"float_right subtle_silver\">{{name}}<\/span>\n";
