TS.raw_templates['fs_modal_file_viewer_header'] = "{{i18n_ns \"file_viewer\"}}\n<header class=\"fs_modal_file_viewer_header{{#if link_url}} external{{\/if}}\">\n\t{{#if member}}\n\t\t<div class=\"star_btn btn_icon btn star_file\">\n\t\t\t{{starWithTip star_type star_ob star_parent_ob}}\n\t\t<\/div>\n\t{{\/if}}\n\n\t{{> file_header_detailed}}\n\n\t<div class=\"controls\">\n\n\t\t{{#if actions.download}}\n\t\t\t<a href=\"{{file.url_private_download}}\" target=\"{{newWindowName}}\" data-file-id=\"{{file.id}}\" class=\"download_btn control_btn btn_icon btn ts_icon ts_icon_cloud_download ts_tip ts_tip_bottom file_ssb_download_link\">\n\t\t\t\t<div class=\"ts_tip_tip\">{{#t}}T\u00e9l\u00e9charger{{\/t}}\n\t\t\t\t\t<div class=\"muted_tooltip_info\">{{makeFileSize file}}<\/div>\n\t\t\t\t<\/div>\n\t\t\t\t<div class=\"flex_menu_download_circle\"><\/div>\n\t\t\t<\/a>\n\t\t{{\/if}}\n\n\t\t{{#if link_url}}\n\t\t\t<a {{makeRefererSafeLink url=link_url}} target=\"_blank\" class=\"open_btn control_btn btn_icon btn ts_icon ts_icon_external_link ts_tip ts_tip_bottom\">\n\t\t\t\t<span class=\"ts_tip_tip\">{{#t}}Ouvrir l\u2019original{{\/t}}<\/span>\n\t\t\t<\/a>\n\t\t{{\/if}}\n\n\t\t{{#if actions.more}}\n\t\t\t<button data-file-id=\"{{file.id}}\"\n\t\t\t\tclass=\"more_btn control_btn btn_icon btn ts_icon ts_icon_ellipsis_o ts_tip ts_tip_bottom file_actions\"\n\t\t\t\tdata-include-view-public-link=\"true\"\n\t\t\t\tdata-exclude-rxn=\"true\"{{! no way to update reactions in this fs modal :( }}\n\t\t\t\tdata-exclude-edit-title=\"true\"{{! don\u2019t show rename changing in flexpane, it\u2019s behind the fs modal }}\n\t\t\t\tdata-exclude-download=\"true\"{{! download is outside menu above \u2191 }}\n\t\t\t\tdata-exclude-comment=\"true\"{{! comments are below \u2193 }}\n\t\t\t>\n\t\t\t\t<span class=\"ts_tip_tip\">{{#t}}Autres actions{{\/t}}<\/span>\n\t\t\t<\/button>\n\t\t{{\/if}}\n\n\t\t{{#if actions.comment}}\n\t\t\t<button class=\"comment_btn control_btn btn ts_tip ts_tip_bottom {{#if is_comment_panel_active}}active{{\/if}}\">\n\t\t\t\t<ts-icon class=\"ts_icon ts_icon_comment_o\"><\/ts-icon>\n\t\t\t\t<span class=\"comment_count\">{{comments_count}}<\/span>\n\t\t\t\t<span class=\"ts_tip_tip\" data-tip-toggle-auto=\"{{#t}}Masquer les commentaires{{\/t}}\">{{#t}}Afficher les commentaires{{\/t}}<\/span>\n\t\t\t<\/button>\n\t\t{{\/if}}\n\n\t\t<button id=\"fs_modal_close_btn\" class=\"close_btn control_btn btn_icon btn ts_icon ts_icon_times ts_tip ts_tip_bottom ts_tip_right\">\n\t\t\t<div class=\"ts_tip_tip\">{{#t}}Fermer{{\/t}}\n\t\t\t\t<div class=\"muted_tooltip_info\">{{#t}}(\u00e9chap){{\/t}}<\/div>\n\t\t\t<\/div>\n\t\t<\/button>\n\n\t<\/div>\n<\/header>\n";
