TS.raw_templates['help_modal'] = "{{i18n_ns \"help\"}}\n{{!-- hbslint-disable syntax --}}\n{{!-- Todo: enable syntax linter and enable safeString --}}\n<div id=\"help_modal_container\" class=\"display_flex flex_direction_column\">\n\t<div class=\"large_bottom_margin display_flex align_items_baseline justify_content_between\">\n<h1 class=\"no_bottom_margin\">{{#t}}Pouvons-nous vous aider\u00a0?{{\/t}}<\/h1>\n\t\t<a id=\"browse_all\" data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=browse_all,trigger=click_browse_all\" class=\"see_all\" href=\"https:\/\/get.slack.help\/hc\/{{zdLocale}}?{{hcTrackingToken}}\" target=\"{{newWindowName}}\">{{#t}}Parcourir toutes les r\u00e9ponses{{\/t}}<ts-icon class=\"small_left_margin ts_icon_arrow_large_right\"><\/ts-icon><\/a>\n\t<\/div>\n\n\t<div class=\"small_bottom_margin help_modal_filter_container\">\n\t\t<ts-icon class=\"ts_icon_search cloud_silver search_icon\"><\/ts-icon>\n\t\t<ts-icon class=\"ts_icon_times_circle cloud_silver clear_filter_icon\"><\/ts-icon>\n\t\t<input type=\"text\" id=\"help_modal_filter\" class=\"small\" placeholder=\"{{#t}}Rechercher dans le Centre d\u2019assistance{{\/t}}\">\n\t<\/div>\n\n\t<div id=\"help_modal_empty\" class=\"hidden align_center margin_auto display_flex align_items_center justify_content_center\">\n\t\t<span class=\"help_modal_empty_message\"><\/span>\n\t<\/div>\n\t<div id=\"help_modal_list_container\" class=\"help_modal_list\">\n\t\t{{{loadingHTML}}}\n\t<\/div>\n<\/div>\n";
TS.raw_templates['help_modal_header'] = "{{i18n_ns \"help\"}}\n\n{{#feature flag=\"feature_shortcuts_flexpane\"}}\n\t<div id=\"help_modal_header\" class=\"help_modal_header\">\n\t\t<a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=keyboard_shorcuts,trigger=click_keyboard_shorcuts\" class=\"keyboard_shortcuts\">\n\t\t\t<ts-icon class=\"ts_icon_keyboard small_right_margin\"><\/ts-icon>\n\t\t\t{{#t}}Raccourcis clavier{{\/t}}\n\t\t<\/a>\n\t<\/div>\n{{else}}\n\t<div id=\"help_modal_header\" class=\"help_modal_header_legacy\">\n\t\t<a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=keyboard_shorcuts,trigger=click_keyboard_shorcuts\" class=\"subtle_silver right_margin keyboard_shortcuts\"><ts-icon class=\"ts_icon_laptop small_right_margin\"><\/ts-icon>{{#t}}Raccourcis clavier...{{\/t}}<\/a>\n\t<\/div>\n{{\/feature}}";
