TS.raw_templates['help_modal_footer_status'] = "{{i18n_ns \"help\"}}\n\n{{#if settings.is_old_tickets}}\n<p id=\"no_open_issues\">\n\t{{#t}}Aucune demande en attente.{{\/t}} <a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=view_request_history,trigger=click_view_request_history\" href=\"\/help\" target=\"{{newWindowName}}\" class=\"bold\">{{#t}}Afficher votre historique de demande d\u2019aide{{\/t}}<\/a>.\n<\/p>\n{{\/if}}\n\n{{#if settings.is_unread}}\n<p id=\"unread_issues\">\n\t{{#if settings.is_unread_many}}\n\t<span id=\"unread_issues_many\">\n\t\t<a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=view_new_replies,trigger=click_view_new_replies\" href=\"\/help\" target=\"{{newWindowName}}\" class=\"bold\"><span class=\"badge candy_red_bg\" id=\"unread_issues_count_txt\">{{settings.unread_count}}<\/span>{{#t}}Vous avez re\u00e7u de nouvelles r\u00e9ponses pour vos demandes d'aide.{{\/t}}<\/a>\n\t<\/span>\n\t{{else}}\n\t<span id=\"unread_issues_singular\">\n        <a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=view_new_replies,trigger=click_view_new_replies\" href=\"\/help\" target=\"{{newWindowName}}\" class=\"bold\"><span class=\"badge candy_red_bg\">1<\/span>{{#t}}Vous avez re\u00e7u une nouvelle r\u00e9ponse pour votre demande d\u2019aide{{\/t}}<\/a>\n\t<\/span>\n\t{{\/if}}\n<\/p>\n{{\/if}}\n\n{{#if settings.is_open}}\n<p id=\"open_issues\" class=\"bottom_margin\">\n\t{{#if settings.is_open_many}}\n\t<span id=\"open_issues_many\">\n\t\t<a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=view_open_requests,trigger=click_view_open_requests\" href=\"\/help\" target=\"{{newWindowName}}\" class=\"bold\"><span class=\"badge seafoam_green_bg\" id=\"open_issues_count_txt\">{{settings.open_count}}<\/span>{{#t}}Vous avez des demandes d\u2019aide ouvertes.{{\/t}}<\/a>\n\t<\/span>\n\t{{else}}\n\t<span id=\"open_issues_singular\">\n\t\t<a data-clog-click=\"true\" data-clog-event=\"HELP_MODAL_ACTION\" data-clog-params=\"action=view_open_requests,trigger=click_view_open_requests\" href=\"\/help\" target=\"{{newWindowName}}\" class=\"bold\"><span class=\"badge seafoam_green_bg\">1<\/span>{{#t}}Vous avez une demande d\u2019aide ouverte.{{\/t}}<\/a>\n\t<\/span>\n\t{{\/if}}\n<\/p>\n{{\/if}}\n";
TS.raw_templates['fs_modal_simple_gallery'] = "{{i18n_ns \"fs_modal\"}}\n{{!--\n\tSimple Gallery Template for FS Modal\n\t---\n\tModeled after the file viewer modal, but less specific to file-viewer needs\n\n\t- To use:\n\t\t- Include this as `body_template_html` template when instantiating fs_modal\n\t\t- Requires: list of {items} - precompiled Handelbars templates for elements to cycle through\n\t\t- Optional: {modal_gallery_index} - where to begin the slideshow\n --}}\n<div class=\"c-modal_gallery__container\" data-current-index=\"{{modal_gallery_index}}\">\n\n\t<button\n\t\tclass=\"c-modal_gallery__nav\"\n\t\tdata-modal-gallery-direction=\"-1\"\n\t\taria-label=\"{{#t note=\"Indicates a link that will take the user to the previous modal view\"}}pr\u00e9c\u00e9dent{{\/t}}\"\n\t>\n\t\t<ts-icon class=\"ts_icon_arrow_large_left\"><\/ts-icon>\n\t<\/button>\n\n\t<div class=\"c-modal_gallery__items\" data-modal-items>\n\t\t{{#each items}}\n\t\t\t<div class=\"c-modal_gallery__item hidden\" data-modal-gallery-index=\"{{@index}}\">\n\t\t\t\t{{dangerouslyRenderHTML this}}\n\t\t\t<\/div>\n\t\t{{\/each}}\n\t<\/div>\n\n\t<button\n\t\tclass=\"c-modal_gallery__nav\"\n\t\tdata-modal-gallery-direction=\"1\"\n\t\taria-label=\"{{#t note=\"Indicates a link that will take the user to the next modal view\"}}suivant{{\/t}}\"\n\t>\n\t\t<ts-icon class=\"ts_icon_arrow_large_right\"><\/ts-icon>\n\t<\/button>\n\n<\/div>\n";
TS.raw_templates['fs_modal_app_header'] = "{{i18n_ns \"fs_modal\"}}\n{{!--\n\tCustom Fullscreen Modal Header for App Screenshots\n --}}\n<div id=\"fs_modal_header\" class=\"p-app_space_modal_header\">\n\t<div class=\"p-app_space_modal_header__avatar\">\n\t\t<img class=\"rounded\"\n\t\t\t{{#if avatar}}\n\t\t\t\tsrc=\"{{avatar}}\"\n\t\t\t{{else}}\n\t\t\t\tsrc=\"{{versioned_default_application_icon}}\"\n\t\t\t{{\/if}}\n\t\t>\n\t<\/div>\n\t<div class=\"p-app_space_modal_header__title\">\n\t\t<div class=\"p-app_space_modal_header__app_name\">{{name}}<\/div>\n\t\t<div class=\"p-app_space_modal_header__subtext\">\n\t\t\t{{#t note=\"App Space modal header featuring examples of how to use app\"}}Vid\u00e9os et captures d\u2019\u00e9cran{{\/t}}\n\t\t\t(<span data-modal-slideshow-count>1<\/span>\/<span data-modal-slideshow-total>1<\/span>)\n\t\t<\/div>\n\t<\/div>\n<\/div>";
TS.raw_templates['email_preview_send'] = "{{!-- hbslint-disable unwrapped --}}\n{{!-- because users won't ever see it, yo --}}\n<form id=\"send_email_preview_form\" action={{url}} method=\"POST\" target=\"dump\">\n\t<input type=\"hidden\" name=\"crumb\" value={{crumb}}><\/input>\n\t{{#if email_on_acid}}\n\t\tThis sends an email to Email On Acid, which will generate email previews on all the different email clients.\n\t\t<a href=\"https:\/\/www.emailonacid.com\/login\">Log in<\/a> to Slack's account to view the email previews.\n\t\tIf you need help using Email on Acid, instructions are pinned to the #emails channel.\n\t\t<input type=\"hidden\" name=\"send\" value=\"iamcal.runme.spam@previews.emailonacid.com\"><\/input>\n\t\t<input type=\"hidden\" name=\"subjectsuffix\" value=\"true\"><\/input>\n\t{{else}}\n\t\tList your recipients\u2019 e-mail addresses here, separating multiple addresses with commas.\n\t\t<br><br>\n\t\t<textarea id=\"recipients\" name=\"send\"><\/textarea>\n\t{{\/if}}\n<\/form>";
TS.raw_templates['onboarding_dialog_welcome_and_completion'] = "{{i18n_ns \"dialogs_onboarding\"}}\n{{#feature flag=\"feature_lesson_onboarding\"}}\n<div class=\"c-modal__media_container--onboarding_dialog full_width position_relative\">\n\t<div id=\"onboarding_media_infinite_spinner\" class=\"infinite_spinner infinite_spinner_medium no_margin position_absolute\">\n\t\t<svg class=\"infinite_spinner_spinner\" viewBox=\"0 0 100 100\">\n\t\t\t<circle class=\"infinite_spinner_bg\" cx=\"50%\" cy=\"50%\" r=\"35\" \/>\n\t\t\t<circle class=\"infinite_spinner_path infinite_spinner_blue\" cx=\"50%\" cy=\"50%\" r=\"35\"\/>\n\t\t<\/svg>\n\t\t<svg class=\"infinite_spinner_spinner infinite_spinner_tail\" viewBox=\"0 0 100 100\">\n\t\t\t<circle class=\"infinite_spinner_path infinite_spinner_blue\" cx=\"50%\" cy=\"50%\" r=\"35\"\/>\n\t\t<\/svg>\n\t<\/div>\n\t<img class=\"c-modal__media--onboarding_dialog full_max_width position_relative\" src=\"{{image_src}}\" \/>\n<\/div>\n<button type=\"button\" class=\"c-modal__close--onboarding_dialog no_padding no_margin no_border position_absolute\" data-dismiss=\"close\">&times;<\/button>\n<div class=\"c-modal__copy--onboarding_dialog align_center full_width\">\n\t<h2 class=\"align_center small_bottom_margin\">{{greeting}}<\/h2>\n\t<p class=\"align_center\">\n\t\t{{description}}\n\t<\/p>\n\t{{#if second_go_button_text}}\n\t\t<button id=\"onboarding_secondary_go_button\" class=\"btn btn_outline c-modal__button--onboarding c-modal__button--onboarding_completed\" type=\"button\">{{second_go_button_text}}<\/button>\n\t{{\/if}}\n\t<button id=\"onboarding_go_button\" class=\"btn c-modal__button--onboarding\" type=\"button\">{{go_button_text}}<\/button>\n<\/div>\n{{\/feature}}\n";
TS.raw_templates['onboarding_dialog_lesson'] = "{{i18n_ns \"dialogs_onboarding\"}}\n{{#feature flag=\"feature_lesson_onboarding\"}}\n\n<div class=\"c-modal__media_container--onboarding_dialog full_width {{modal_color_class}} position_relative\">\n\t<div id=\"onboarding_media_infinite_spinner\" class=\"infinite_spinner infinite_spinner_medium no_margin position_absolute\">\n\t\t<svg class=\"infinite_spinner_spinner\" viewBox=\"0 0 100 100\">\n\t\t\t<circle class=\"infinite_spinner_bg infinite_spinner_bg_white\" cx=\"50%\" cy=\"50%\" r=\"35\" \/>\n\t\t\t<circle class=\"infinite_spinner_path infinite_spinner_white\" cx=\"50%\" cy=\"50%\" r=\"35\"\/>\n\t\t<\/svg>\n\t\t<svg class=\"infinite_spinner_spinner infinite_spinner_tail\" viewBox=\"0 0 100 100\">\n\t\t\t<circle class=\"infinite_spinner_path infinite_spinner_white\" cx=\"50%\" cy=\"50%\" r=\"35\"\/>\n\t\t<\/svg>\n\t<\/div>\n\t<button class=\"c-modal__left_navigation--onboarding_dialog position_absolute no_border no_padding\">\n\t\t<ts-icon class=\"ts_icon_chevron_left c-modal__left_caret--onboarding_dialog position_absolute\"><\/ts-icon>\n\t<\/button>\n\t<button class=\"c-modal__right_navigation--onboarding_dialog position_absolute no_border no_padding\">\n\t\t<ts-icon class=\"ts_icon_chevron_right c-modal__right_caret--onboarding_dialog position_absolute\"><\/ts-icon>\n\t<\/button>\n<\/div>\n\n{{!-- this button must come after .c-modal__media_container--onboarding_dialog to appear on top and be visible --}}\n<button type=\"button\" class=\"c-modal__close--onboarding_dialog c-modal__close--onboarding_dialog_white no_padding no_margin no_border position_absolute\" data-dismiss=\"close\">&times;<\/button>\n\n<div class=\"c-modal__copy--onboarding_dialog full_width align_right\">\n\t<div class=\"c-modal__lesson_circle_buttons\">\n\t\t{{#each lesson_circle_classes}}\n\t\t\t<button class=\"c-modal__lesson_circle_button no_padding no_border\">\n\t\t\t\t<ts-icon class=\"{{this}}\"><\/ts-icon>\n\t\t\t<\/button>\n\t\t{{\/e