TS.raw_templates['prefs_notifications_summary'] = "{{i18n_ns \"prefs\"}}\n\n<span class=\"small_right_margin no_wrap\">\n\t{{#t}}\n\t\t<strong class=\"black\">Envoyer des notifications pour\u00a0\u00a0:<\/strong>\n\t\t{ send_notifications_for, select,\n\t\t\tall { toutes les activit\u00e9s }\n\t\t\tmentions { messages directs et mots-cl\u00e9s }\n\t\t\tother { rien }\n\t\t}\n\t{{\/t}}\n<\/span>\n\n<span class=\"small_right_margin no_wrap\">\n\t<strong class=\"black\">{{#t}}Alerte sonore\u00a0:{{\/t}}<\/strong>\n\t{{#if mute_sounds}}{{#t}}D\u00e9sactiv\u00e9{{\/t}}{{else}}{{new_msg_snd}}{{\/if}}\n<\/span>\n\n<span class=\"small_right_margin no_wrap\">\n\t<strong class=\"black\">{{#t}}Mode d\u2019affichage\u00a0:{{\/t}}<\/strong>\n\t{{#if no_text_in_notifications}}{{#t}}Masquer le texte{{\/t}}{{else}}{{#t}}Afficher le texte{{\/t}}{{\/if}}\n<\/span>\n\n{{#if show_mac_ssb_prefs}}\n\t<span class=\"small_right_margin no_wrap\">\n\t\t<strong class=\"black\">{{#t}}Ic\u00f4ne de l\u2019application du Dock\u00a0:{{\/t}}<\/strong>\n\t\t{{#if show_mac_ssb_bullet}}\n\t\t\t{{#t}}\n\t\t\t\tAfficher l\u2019ic\u00f4ne {mac_ssb_bounce, select,\n\t\t\t\t\ttrue { & rebondir { mac_ssb_bounce_short, select, true { une fois } other {} } }\n\t\t\t\t\tother {}\n\t\t\t\t}\n\t\t\t{{\/t}}\n\t\t{{else}}\n\t\t\t{{#t}}\n\t\t\t\tMasquer l'ic\u00f4ne {mac_ssb_bounce, select,\n\t\t\t\t\ttrue { & bounce { mac_ssb_bounce_short, select, true { une fois } other {} } }\n\t\t\t\t\tother {}\n\t\t\t\t}\n\t\t\t{{\/t}}\n\t\t{{\/if}}\n\t<\/span>\n{{\/if}}\n\n{{#if show_win_ssb_prefs}}\n\t<span class=\"small_right_margin\">\n\t\t{{#t}}\n\t\t\t<strong class=\"black\">Faire clignoter la fen\u00eatre\u00a0:<\/strong>\n\t\t\t{ window_flash_behavior, select,\n\t\t\t\tnever { jamais }\n\t\t\t\tidle { Quand inactive }\n\t\t\t\tother { Toujours }\n\t\t\t}\n\t\t{{\/t}}\n\n\t\t{{#if show_notify_position}}\n\t\t\t{{#if_not_equal window_flash_behavior compare=\"never\"}}\n\t\t\t\t{{#t}}\n\t\t\t\t\t(afficher les notifications { winssb_notify_corner, select,\n\t\t\t\t\t\ttop_left { en haut \u00e0 gauche }\n\t\t\t\t\t\ttop_right { en haut \u00e0 droite }\n\t\t\t\t\t\tbottom_left { en bas \u00e0 gauche }\n\t\t\t\t\t\tother { en bas \u00e0 droite }\n\t\t\t\t\t} { winssb_notify_display, select,\n\t\t\t\t\t\tsame_as_app { du m\u00eame \u00e9cran que l'appli }\n\t\t\t\t\t\tother { de l'\u00e9cran principal }\n\t\t\t\t\t})\n\t\t\t\t{{\/t}}\n\t\t\t{{\/if_not_equal}}\n\t\t{{\/if}}\n\t<\/span>\n{{\/if}}\n";
TS.raw_templates['prefs_dnd_summary'] = "{{i18n_ns 'dialogs'}}\n\n<span class=\"small_right_margin\">\n\t{{#if dnd_enabled}}\n\t\t{{#t}}\n\t\t<strong class=\"black\">Les notifications ont \u00e9t\u00e9 d\u00e9sactiv\u00e9es<\/strong>\n\t\tde {dnd_start_hour} \u00e0 {dnd_end_hour} <span class=\"dnd_tz_label\">{timezone}<\/span>\n\t\t{{\/t}}\n\t{{else}}\n\t\t{{#t}}La fonction Ne pas d\u00e9ranger n'est pas activ\u00e9e. Nous nous en tiendrons \u00e0 vos param\u00e8tres de notification.{{\/t}}\n\t{{\/if}}\n<\/span>\n";
TS.raw_templates['prefs_win_lin_ssb'] = "{{i18n_ns 'win_prefs'}}\n\n<h2 id=\"prefs_app_settings\" class=\"inline_block\">{{#t}}Param\u00e8tres de l'application{{\/t}}<\/h2>\n<div>\n\t{{#if show_auto_launch_pref}}\n\t\t<label class=\"checkbox\">\n\t\t\t<input id=\"winssb_launch_on_start_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_app_settings\">\n\t\t\t{{#t}}Lancer l'application lors de la connexion{{\/t}}\n\t\t<\/label>\n\t{{\/if}}\n\t<label class=\"checkbox\">\n\t\t<input id=\"winssb_run_from_tray_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_app_settings\">\n\t\t{{#t}}Laisser l\u2019application s'ex\u00e9cuter dans la zone de notification lorsque la fen\u00eatre est ferm\u00e9e{{\/t}}\n\t<\/label>\n\t<div id=\"winssb_flash_warning\" class=\"alert alert_warning\">\n\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t<p>{{#t}}En raison de vos param\u00e8tres de notification, Slack reste dans votre barre des t\u00e2ches m\u00eame lorsqu\u2019il est ferm\u00e9.{{\/t}}<\/p>\n\t\t<p class=\"no_bottom_margin\">\n\t\t\t{{#t}}Pour changer cela, acc\u00e9dez \u00e0 <strong>Notifications \u2192 Sons et apparence<\/strong>. Vous pouvez ensuite\u2026\n\t\t\t<ul>\n\t\t\t\t<li>D\u00e9sactiver le param\u00e8tre de fen\u00eatre Slack clignotante lors de la r\u00e9ception d\u2019une notification.<\/li>\n\t\t\t\t<li>Ou laisser le param\u00e8tre activ\u00e9, mais s\u00e9lectionner l\u2019option de fen\u00eatre clignotante uniquement lorsque vous n\u2019utilisez pas votre ordinateur.<\/li>\n\t\t\t<\/ul>{{\/t}}\n\t\t<\/p>\n\t<\/div>\n\t<label class=\"checkbox\" id=\"winssb_disable_hw_acceleration_pref\">\n\t\t<input id=\"winssb_disable_hw_acceleration_cb\" checked=\"unchecked\" type=\"checkbox\" data-inline-saver=\"#prefs_app_settings\">\n\t\t{{#t}}D\u00e9sactiver l'acc\u00e9l\u00e9ration mat\u00e9rielle{{\/t}}<br>\n\t\t<span id=\"winssb_disable_hw_acceleration_note\" class=\"normal\">{{#unless show_lin_ssb_prefs}}{{#t}}Si vous utilisez un th\u00e8me classique de Windows, cela peut r\u00e9soudre les probl\u00e8mes de rendu.  {{\/t}}{{\/unless}}{{#t}}Red\u00e9marrez Slack pour que cette pr\u00e9f\u00e9rence prenne effet.{{\/t}}<\/span>\n\t<\/label>\n<\/div>\n";
TS.raw_templates['prefs_language_region'] = "{{i18n_ns \"prefs\"}}\n\n<h2>{{#t}}Langue et r\u00e9gion{{\/t}}<\/h2>\n\n<label class=\"select\">\n\t<h4 class=\"prefs_lang inline_block\">{{#t}}Langue{{\/t}}<\/h4>\n\t<select name=\"lang\" data-inline-saver='.prefs_lang'>\n\t\t{{#each languages}}\n\t\t\t<option value={{@key}} {{#if_equal @key compare=..\/user_locale}}selected=\"selected\" aria-selected=\"true\"{{\/if_equal}}>{{this}}<\/option>\n\t\t{{\/each}}\n\t<\/select>\n<\/label>\n\n<p class=\"normal greigh large_bottom_margin\">{{#t}}S\u00e9lectionnez la langue que vous voulez utiliser dans Slack.{{\/t}}<\/p>\n\n<label class=\"select bottom_margin\">\n\t<h4 class=\"prefs_tz inline_block\">{{#t}}Fuseau horaire{{\/t}}<\/h4>\n\t<select name=\"tz\" data-inline-saver='.prefs_tz'>\n\t\t{{#each timezones}}\n\t\t\t<option value={{this.[1]}} {{#if_equal this.[1] compare=..\/user_tz}}selected=\"selected\" aria-selected=\"true\"{{\/if_equal}}>{{this.[0]}}<\/option>\n\t\t{{\/each}}\n\t<\/select>\n<\/label>\n\n{{#if show_keyboard}}\n<label class=\"select\">\n\t<h4 class=\"prefs_keyboard inline_block\">{{#t}}Disposition du clavier{{\/t}}<\/h4>\n\t<select name=\"keyboard\" data-inline-saver='.prefs_keyboard'>\n\t\t{{#each supported_keyboards}}\n\t\t\t<option value={{@key}} {{#if_equal @key compare=..\/user_keyboard}}selected=\"selected\" aria-selected=\"true\"{{\/if_equal}}>{{this}}<\/option>\n\t\t{{\/each}}\n\t<\/select>\n<\/label>\n\n<p class=\"normal greigh\">{{#t}}Modifier cela pour faire correspondre \u00e0 la disposition de votre clavier.{{\/t}}<\/p>\n{{\/if}}\n";
TS.raw_templates['prefs_messages_media'] = "{{i18n_ns \"prefs\"}}\n\n<h2>{{#t}}Messages{{\/t}}<\/h2>\n\n<h4 id=\"prefs_message_theme\" class=\"inline_block\">{{#t}}Th\u00e8me{{\/t}}<\/h4>\n<div class=\"bottom_margin display_flex\">\n\t<div class=\"message_theme_options\">\n\t\t<label class=\"radio\">\n\t\t\t<input name=\"messages_theme_select\" type=\"radio\" value=\"light_with_avatars\" data-inline-saver=\"#prefs_message_theme\">\n\t\t\t{{#t}}Net\u00a0: <span class=\"normal\">clair, facile d\u2019utilisation et allant \u00e0 l\u2019essentiel.<\/span>{{\/t}}\n\t\t<\/label>\n\t\t<label class=\"radio\">\n\t\t\t<input name=\"messages_theme_select\" type=\"radio\" value=\"dense\" data-inline-saver=\"#prefs_message_theme\">\n\t\t\t{{#t}}Compact\u00a0: <span class=\"normal\"> tous les messages que votre \u00e9cran peut contenir.<\/span>{{\/t}}\n\t\t<\/label>\n\t<\/div>\n\t{{#unless hide_previews}}\n\t\t<div class=\"message_theme_preview\">\n\t\t\t{{> prefs_messages_media_message avatar=\"\/img\/slackbot_72.png\" name=\"slackbot\" message=slackbot_message}}\n\t\t<\/div>\n\t{{\/unless}}\n<\/div>\n\n<h4 id=\"prefs_display_options\" class=\"inline_block\">{{#t}}Noms{{\/t}}<\/h4>\n<div class=\"large_bottom_margin\">\n\t<div class=\"global_notification_block no_padding\">\n\t\t<div class=\"display_flex display_real_names_block\">\n\t\t\t<div class=\"small_right_margin\">\n\t\t\t\t<input type=\"radio\" name=\"display_real_names_override_cb\" value=\"real_names\" id=\"display_real_names_override_true\"  data-inline-saver=\"#prefs_display_options\">\n\t\t\t<\/div>\n\t\t\t<div class=\"very_small_top_margin\">\n\t\t\t\t<label for=\"display_real_names_override_true\"><strong>{{#t}}Nom complet et nom d\u2019affichage{{\/t}}<\/strong><\/label>\n\t\t\t\t<span>{{#t}}Montrer les noms d\u2019affichage dans les conversations et les noms complets partout ailleurs <span class=\"display_real_names_default bold\">(param\u00e8tre par d\u00e9faut de l'espace de travail)<\/span>{{\/t}}<\/span>\n\t\t\t<\/div>\n\t\t<\/div>\n\t\t{{#unless hide_previews}}\n\t\t\t<div aria-hidden=\"true\" class=\"display_flex flex_one align_items_center left_margin display_real_names_block_sample\">\n\t\t\t\t{{> prefs_messages_media_message avatar=name_preview_avatar name=name_preview_full_name message=name_preview_message}}\n\t\t\t<\/div>\n\t\t{{\/unless}}\n\t<\/div>\n\t<div class=\"global_notification_block no_padding small_top_margin small_bottom_margin\">\n\t\t<div class=\"display_flex display_real_names_block\">\n\t\t\t<div class=\"small_right_margin\">\n\t\t\t\t<input type=\"radio\" name=\"display_real_names_override_cb\" value=\"display_names\" id=\"display_real_names_override_false\"  data-inline-saver=\"#prefs_display_options\">\n\t\t\t<\/div>\n\t\t\t<div class=\"very_small_top_margin\">\n\t\t\t\t<label for=\"display_real_names_override_false\"><strong>{{#t}}Afficher uniquement les noms{{\/t}}<\/strong><\/label>\n\t\t\t\t<span>{{#t}}Toujours montrer les noms d\u2019affichage <span class=\"display_usernames_default bold\"> (param\u00e8tre par d\u00e9faut de l\u2019espace de travail)<\/span>{{\/t}}<\/span>\n\t\t\t<\/div>\n\t\t<\/div>\n\t\t{{#unless hide_previews}}\n\t\t\t<div aria-hidden=\"true\" class=\"display_flex flex_one align_items_center left_margin display_real_names_block_sample\">\n\t\t\t\t{{> prefs_messages_media_message avatar=name_preview_avatar name=name_preview_display_name message=name_preview_message}}\n\t\t\t<\/div>\n\t\t{{\/unless}}\n\t<\/div>\n\t<p class=\"large_bottom_margin\">{{#t}}Pour modifier votre nom complet ou votre nom d\u2019affichage, acc\u00e9dez \u00e0 <a data-action=\"edit_member_profile_modal\">votre profil<\/a>.{{\/t}}<\/p>\n\n\t<h4 id=\"prefs_display_options_additional\" class=\"inline_block\">{{#t}}Options suppl\u00e9mentaires{{\/t}}<\/h4>\n\t<label class=\"checkbox\">\n\t\t<input id=\"show_typing_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\t{{#t}}Afficher les informations concernant les utilisateurs en train de saisir un message{{\/t}}\n\t<\/label>\n\t{{#if show_time24_pref}}\n\t\t<label class=\"checkbox\">\n\t\t\t<input id=\"time24_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\t\t{{#t}}Afficher l'heure au format 24\u00a0heures <span class=\"normal\">(16:00 au lieu de 4:00\u00a0PM)<\/span>{{\/t}}\n\t\t<\/label>\n\t{{\/if}}\n\t<label class=\"checkbox\">\n\t\t<input id=\"hide_hex_swatch_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_display_options\">\n\t\t{{#t}}Afficher les \u00e9chantillons de couleur \u00e0 c\u00f4t\u00e9 des valeurs hexad\u00e9cimales{{\/t}}\n\t<\/label>\n<\/div>\n\n<h4 id=\"prefs_emoji_style\" class=\"inline_block\">{{#t}}Style des \u00e9mojis{{\/t}}<\/h4>\n<br>\n\t<label class=\"checkbox\">\n\t\t<input id=\"plaintext_emoji_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_emoji_style\">\n\t\t{{#t}}Afficher l\u2019\u00e9moji en texte brut{{\/t}}<\/span>\n\t<\/label>\n\t<label id=\"jumbomoji_cb_label\" class=\"checkbox {{#if_equal messages_theme compare=\"dense\"}}jumbomoji_pref_disabled{{\/if_equal}}\">\n\t\t<input id=\"jumbomoji_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_emoji_style\" {{#if_equal messages_theme compare=\"dense\"}}disabled=\"disabled\" aria-disabled=\"true\"{{\/if_equal}}>\n\t\t{{#t}}Afficher les JUMBOMOJIS<br>\n\t\t<span class=\"normal\">Afficher les versions jumbo des \u00e9mojis (jusqu\u2019\u00e0 23 en m\u00eame temps\u00a0!) dans des messages sans texte.<\/span>\n\t\t<span class=\"jumbomoji_disabled_note\"><strong>Remarque\u00a0:<\/strong> En mode compact, tous les \u00e9mojis conservent une taille plus petite et pratique.<\/span>{{\/t}}\n\t<\/label>\n\t<label id=\"convert_emoticons_label\" class=\"checkbox\">\n\t\t<input id=\"convert_emoticons_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_convert_emoticons\">\n\t\t{{#t smile_emoji=(emojiGraphicReplace \":smile:\")}}Convertir mes \u00e9motic\u00f4nes saisies en \u00e9mojis\u00a0(:D\u00a0devient {smile_emoji}){{\/t}}\n\t<\/label>\n<hr>\n\n<h2 id=\"prefs_inline_media\" class=\"large_top_margin inline_block\">{{#t}}M\u00e9dias et liens incorpor\u00e9s{{\/t}}<\/h2>\n<p>\n\t<label class=\"checkbox\">\n\t\t<input id=\"expand_internal_inline_imgs_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\t{{#t}}Afficher les images et fichiers charg\u00e9s dans Slack{{\/t}}\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"expand_inline_imgs_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\t{{#t}}Afficher les images et fichiers des sites web li\u00e9s{{\/t}}\n\t<\/label>\n\t<label class=\"checkbox medium_left_margin\">\n\t\t<input id=\"dont_obey_inline_img_limit_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\t{{#t file_size_limit=(convertFilesize inline_img_byte_limit)}}M\u00eame si la taille est sup\u00e9rieure \u00e0 {file_size_limit}{{\/t}}\n\t<\/label>\n\t<label class=\"checkbox\">\n\t\t<input id=\"expand_non_media_attachments_cb\" checked=\"checked\" type=\"checkbox\" data-inline-saver=\"#prefs_inline_media\">\n\t\t{{#t}}Afficher les aper\u00e7us des textes des sites Web li\u00e9s{{\/t}}\n\t<\/label>\n<\/p>\n\n{{#feature flag=\"feature_email_forwarding\"}}\n{{#if enable_email_forwarding}}\n\n\t<hr>\n\n\t<h2 id=\"prefs_email_forwarding\" class=\"large_top_margin inline_block\">{{#t}}Transf\u00e9rez des e-mail dans Slack{{\/t}}<\/h2>\n\t{{> prefs_email_forwarding loading=\"true\"}}\n\n{{\/if}}\n{{\/feature}}\n";
