TS.raw_templates['prefs_search'] = "{{i18n_ns \"dialogs_prefs_search\"}}\n\n<h2 id=\"prefs_search_exclusion\" class=\"inline_block\">{{#t note=\"heading for preferences related to searching in Slack\"}}Recherche{{\/t}}<\/h2>\n<p>{{#t}}Par d\u00e9faut, Slack effectue les recherches dans toutes les cha\u00eenes. Vous pouvez modifier cela si vous le souhaitez.{{\/t}}<\/p>\n<p>\n\t<label for=\"search_channel_exclusion\">{{#t}}Ne pas effectuer de recherche dans les cha\u00eenes suivantes\u00a0:{{\/t}}<\/label>\n\t<select multiple=\"multiple\" id=\"search_channel_exclusion\" size=\"30\" data-inline-saver=\"#prefs_search_exclusion\" {{#feature flag=\"feature_lazy_channels\"}}class=\"hidden\"{{\/feature}}>\n\t{{#feature flag=\"feature_lazy_channels\"}}{{else}}\n\t\t<optgroup label=\"{{#t note=\"heading for a list of channels\"}}Cha\u00eenes actives{{\/t}}\">\n\t\t\t{{#each active_channels}}\n\t\t\t\t<option value=\"{{this.channel.id}}\"{{#if this.search_channel_exclusion}} selected{{\/if}}>#{{this.channel.name}}<\/option>\n\t\t\t{{\/each}}\n\t\t<\/optgroup>\n\t\t<optgroup label=\"{{#t note=\"heading for a list of channels\"}}Cha\u00eenes archiv\u00e9es{{\/t}}\">\n\t\t\t{{#each archived_channels}}\n\t\t\t\t<option value=\"{{this.channel.id}}\"{{#if this.search_channel_exclusion}} selected{{\/if}}>#{{this.channel.name}}<\/option>\n\t\t\t{{\/each}}\n\t\t<\/optgroup>\n\t{{\/feature}}\n\t<\/select>\n<\/p>";
TS.raw_templates['prefs_read_state_tracking'] = "{{i18n_ns \"dialogs_prefs_read_state_tracking\"}}\n\n<h2 id=\"prefs_read_state_tracking\" class=\"inline_block\">{{#t note=\"heading for read-state preferences\"}}Marquer une cha\u00eene comme lue{{\/t}}<\/h2>\n<p>{{#t}}Slack marque une cha\u00eene comme lue d\u00e8s que vous la consultez. Vous pouvez modifier ce r\u00e9glage si vous le souhaitez.{{\/t}}<\/p>\n\n<h4>{{#t}}Lorsque je consulte une cha\u00eene\u00a0:{{\/t}}<\/h4>\n<p class=\"large_bottom_margin\">\n\t<label class=\"radio\">\n\t\t<input name=\"read_rd\" type=\"radio\" value=\"immediate_scroll\" data-inline-saver=\"#prefs_read_state_tracking\">\n\t\t{{#t}}Commencer o\u00f9 je me suis arr\u00eat\u00e9(e) et marquer la cha\u00eene comme lue{{\/t}}\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"read_rd\" type=\"radio\" value=\"immediate\" data-inline-saver=\"#prefs_read_state_tracking\">\n\t\t{{#t}}Commencer \u00e0 partir du message le plus r\u00e9cent et marquer la cha\u00eene comme lue{{\/t}}\n\t<\/label>\n\t<label class=\"radio\">\n\t\t<input name=\"read_rd\" type=\"radio\" value=\"default\" data-inline-saver=\"#prefs_read_state_tracking\">\n\t\t{{#t}}Afficher le message le plus r\u00e9cent, mais marquer les messages non consult\u00e9s comme non lus{{\/t}}\n\t<\/label>\n<\/p>\n\n<h4>{{#t note=\"heading for read-state shortcuts\"}}Raccourcis clavier{{\/t}}<\/h4>\n<p class=\"small_bottom_margin\">\n\t{{emojiGraphicReplace \":information_desk_person::skin-tone-2:\"}}\n\t{{#t}}<b>Marquez une cha\u00eene comme lue<\/b> en appuyant sur la touche <span class=\"c-keyboard_key c-keyboard_key--slim\">\u00c9chap<\/span>.{{\/t}}\n<\/p>\n<p class=\"small_bottom_margin\">\n\t{{emojiGraphicReplace \":ok_woman::skin-tone-4:\"}}\n\t{{#t}}<b>Marquez tout comme lu<\/b> en appuyant sur <span class=\"c-keyboard_key c-keyboard_key--slim\">Maj<\/span><span class=\"c-keyboard_key c-keyboard_key--slim\">\u00c9chap<\/span>.{{\/t}}\n<\/p>\n<p>\n\t{{emojiGraphicReplace \"