TS.raw_templates['account_secret_codes_modal'] = "{{i18n_ns 'account'}}\n\n<p class=\"alert alert_warning\">\n\t<i class=\"ts_icon ts_icon_exclamation_circle code_warning\"><\/i>{{#t}}Si vous perdez acc\u00e8s \u00e0 votre appareil d\u2019authentification. vous pouvez utiliser l\u2019un de ces codes de secours pour vous connecter \u00e0 votre compte. Chaque code ne peut \u00eatre utilis\u00e9 qu'une seule fois. Faites une copie de ces codes et conservez-les en lieu s\u00fbr.{{\/t}}\n<\/p>\n<div class=\"align_center\">\n\t<div class=\"backup_codes_wrapper margin_auto curved_shadow\">\n\t\t<div class=\"backup_codes\">\n\t\t\t<ul class=\"clearfix\">\n\t\t\t\t{{#each codes}}<li>{{this}}<br><\/li>{{\/each}}\n\t\t\t<\/ul>\n\t\t<\/div>\n\t<\/div>\n<\/div>";
TS.raw_templates['basic_share_dialog'] = "{{i18n_ns \"share_dialog\"}}\n\n<div id=\"share_dialog_input_container\"><\/div>\n\n<span id=\"select_share_at_channel_blocked_note\" class=\"modal_input_note indifferent_grey hidden\"><\/span>\n<span id=\"select_share_at_channel_note\" class=\"modal_input_note indifferent_grey hidden\"><\/span>\n<span id=\"select_share_pick_channel_note\" class=\"modal_input_note indifferent_grey hidden\"><i class=\"ts_icon ts_icon_warning ts_icon_inherit yolk_orange\"><\/i> {{#t}}Veuillez s\u00e9lectionner une cha\u00eene{{\/t}}<\/span>\n\n<div class=\"share_dialog_attachment_container\">\n\t{{attachment_html}}\n<\/div>\n\n<hr class=\"share_dialog_divider\">\n\n<div id=\"file_sharing_div\">\n\t<label for=\"select_share_channels\" class=\"share_prefix\">\n\t\t{{#t fallback_hash=\"5ec9927f96c558e3642b4d546543ae06daeb1d88\" fallback_hash_ns=\"file_share\"}}Partager dans\u00a0:{{\/t}}\n\t<\/label>\n\t<div id=\"select_share_channels\"><\/div>\n<\/div>\n\n{{#if same_channel}}\n\t<span id=\"share_dialog_same_channel\">{{same_channel}}<\/span>\n{{\/if}}\n\n{{#if show_copy_link}}\n\t<button class=\"hidden btn btn_outline ts_icon ts_icon_link ts_tip ts_tip_top ts_tip_float ts_tip_align_left ts_tip_multiline\" data-action=\"copy_link\">\n\t\t<span class=\"ts_tip_tip\">\n\t\t\t<span class=\"ts_tip_multiline_inner\">{{copy_text}}<\/span>\n\t\t<\/span>\n\t<\/button>\n{{\/if}}\n\n<div id=\"share_dialog_warning\" class=\"hidden clear_both c-alert c-alert--inline c-alert--level_warning\">\n\t<span id=\"share_dialog_warning_msg\" class=\"c-alert__message\"><\/span>\n<\/div>\n";
