TS.raw_templates['file_sharing_channel_row'] = "{{i18n_ns 'file_share'}}\n\n{{#if item.create_channel}}\n\t{{#t}}Cr\u00e9er une cha\u00eene\u00a0:{{\/t}} <i class=\"ts_icon ts_icon_channel\"><\/i><strong><span class=\"hash\">#<\/span>{{item.name}}<\/strong>\n{{else if item.is_mpim}}\n\t<i class=\"ts_icon multipary_dm_icon ts_icon_multiparty_dm_{{mpimMemberCount item}}\"><\/i>{{mpimDisplayName item}}\n{{else if item.is_group}}\n\t\t<i class=\"ts_icon ts_icon_lock\"><\/i>{{item.name}} {{#if item.is_org_shared}}{{> shared_channel_icon}}{{else}}{{#isModelObExtShared item}}{{> shared_channels_icon model_ob=item}}{{\/isModelObExtShared}}{{\/if}}\n{{else if item.is_channel}}\n\t{{#if item.is_private}}\n\t\t<i class=\"ts_icon ts_icon_lock\"><\/i>{{item.name}} {{#if item.is_org_shared}}{{> shared_channel_icon}}{{else}}{{#isModelObExtShared item}}{{> shared_channels_icon model_ob=item}}{{\/isModelObExtShared}}{{\/if}}\n\t{{else}}\n\t\t<i class=\"ts_icon ts_icon_channel_pane_hash\"><\/i>{{item.name}} {{#if item.is_org_shared}}{{> shared_channel_icon}}{{else}}{{#isModelObExtShared item}}{{> shared_channels_icon model_ob=item}}{{\/isModelObExtShared}}{{\/if}}\n\t{{\/if}}\n{{else}}\n\t{{> c-unified_member member=item size=\"small\" avatar=false short=true no_tip=true}}\n{{\/if}}\n";
