TS.raw_templates['shared_channels_leave_open_beta_dialog'] = "{{i18n_ns 'shared'}}\n\n\t{{#t note=\"confirm dialog outlining what will happen when your team leaves the shared channels open beta\"}}\n\t\t<p>Ceci aura pour effet de retirer <strong>votre espace de travail<\/strong> du programme des cha\u00eenes partag\u00e9es en version b\u00eata.<\/p>\n\t\t<ul class=\"greigh\">\n\t\t\t<li><strong>Les administrateurs ne pourront plus cr\u00e9er de cha\u00eenes partag\u00e9es<\/strong> ni accepter des invitations \u00e0 rejoindre de nouvelles cha\u00eenes partag\u00e9es.<\/li>\n\t\t\t<li><strong>Les cha\u00eenes partag\u00e9es existantes seront maintenues.<\/strong> Vous aurez la possibilit\u00e9 de retirer votre espace de travail de cha\u00eenes partag\u00e9es sp\u00e9cifiques si vous le souhaitez.<\/li>\n\t\t<\/ul>\n\t{{\/t}}\n";
TS.raw_templates['shared_channels_open_beta_modal'] = "{{i18n_ns 'shared'}}\n\n<div class=\"shared_channels_open_beta_modal\">\n\t<div class=\"shared_channels_open_beta_modal_image\"><\/div>\n\n\t<h2 class=\"shared_channels_open_beta_modal_header\">\n\t\t{{#t}}Pr\u00e9sentation des cha\u00eenes partag\u00e9es{{\/t}}\n\t<\/h2>\n\n\t<div class=\"shared_channels_open_beta_modal_body\">\n\t\t<p class=\"shared_channels_open_beta_modal_bullet\">\n\t\t\t<ts-icon class=\"ts_icon_check_small_bold\"><\/ts-icon>\n\t\t\t{{#t}}<strong>Un nouvel esprit au travail.<\/strong> Les cha\u00eenes partag\u00e9es vous permettent de travailler avec d\u2019autres entreprises et d\u2019autres \u00e9quipes directement sur Slack.{{\/t}}\n\t\t<\/p>\n\t\t<p class=\"shared_channels_open_beta_modal_bullet\">\n\t\t\t<ts-icon class=\"ts_icon_check_small_bold\"><\/ts-icon>\n\t\t\t{{#t}}<strong>Un seul endroit pour tout.<\/strong> Sur des projets impliquant plusieurs entreprises, les cha\u00eenes partag\u00e9es sont une source fiable unique accessible par la fonctionnalit\u00e9 de recherche.{{\/t}}\n\t\t<\/p>\n\t\t<p class=\"shared_channels_open_beta_modal_bullet\">\n\t\t\t<ts-icon class=\"ts_icon_check_small_bold\"><\/ts-icon>\n\t\t\t{{#t}}<strong>S\u00fbr et limit\u00e9.<\/strong> Seuls les administrateurs peuvent partager une cha\u00eene. Le reste de votre espace de travail reste priv\u00e9.{{\/t}}\n\t\t<\/p>\n\t\t<p class=\"shared_channels_open_beta_modal_bullet\">\n\t\t\t<ts-icon class=\"ts_icon_exclamation_small\"><\/ts-icon>\n\t\t\t{{#t}}<strong>Actuellement en version b\u00eata.<\/strong> \u00c0 l'heure actuelle, certaines des fonctionnalit\u00e9s - comme les r\u00e8gles de conservation personnalis\u00e9es - ne s'appliquent pas aux cha\u00eenes partag\u00e9es. <a href=\"https:\/\/get.slack.help\/hc\/articles\/115004152843\">En savoir plus<\/a>{{\/t}}\n\t\t<\/p>\n\t<\/div>\n<\/div>\n";
TS.raw_templates['shared_channels_help_content'] = "{{i18n_ns 'shared'}}\n\n<div class=\"shared_channels_help_content\">\n\t<div class=\"shared_channels_help_content_icon\">\n\t\t<ts-icon class=\"ts_icon_book\"><\/ts-icon>\n\t<\/div>\n\t<div class=\"shared_channels_help_content_text\">\n\t\t<div>{{#t}}Vous souhaitez en savoir plus sur les cha\u00eenes partag\u00e9es\u00a0?{{\/t}}<\/div>\n\t\t<div>{{#t}}Acc\u00e9dez au <a class=\"no_underline\" href=\"https:\/\/get.slack.help\/hc\/articles\/115004151183\">centre d\u2019assistance de Slack<\/a>.{{\/t}}<\/div>\n\t<\/div>\n<\/div>\n";
TS.raw_templates['shared_channels_invites_modal'] = "{{i18n_ns \"shared_channels_invite\"}}\n<div id=\"sci_container\" class=\"channel_modal_with_list channel_modal {{#feature flag='feature_channel_browser_dropdown'}}feature_channel_browser_dropdown{{\/feature}}\">\n\n\t<div id=\"sci_header_wrapper\">\n\t\t<div class=\"sci_header_container large_bottom_margin display_flex align_items_center\">\n\t\t\t<h1 class=\"sci_header\">{{#t}}Cha\u00eenes partag\u00e9es{{\/t}}<\/h1>\n\n\t\t\t{{#if show_create_shared_channel_btn}}<button class=\"btn auto_left_margin\" data-action=\"sci_to_create\">{{#t}}Nouvelle cha\u00eene partag\u00e9e{{\/t}}<\/button>{{\/if}}\n\t\t<\/div>\n\n\t\t<div class=\"small_bottom_margin display_flex align_items_center\">\n\t\t\t<div class=\"channel_browser_filter_container channel_modal_filter_container flex_one\">\n\t\t\t\t<i class=\"ts_icon ts_icon_search cloud_silver search_icon\"><\/i>\n\t\t\t\t<i class=\"ts_icon ts_icon_times_circle cloud_silver clear_filter_icon\"><\/i>\n\t\t\t\t<input type=\"text\" id=\"channel_browser_filter\" class=\"small\" placeholder=\"{{#t}}Rechercher des cha\u00eenes{{\/t}}\">\n\t\t\t<\/div>\n\t\t<\/div>\n\t\t<div class=\"small_bottom_margin display_flex align_items_center\">\n\t\t\t<div class=\"channel_browser_sort_container flex_one\">\n\t\t\t\t<select id=\"channel_browser_sort\" class=\"small display_none\">\n\t\t\t\t\t<option value=\"name\">{{#t}}Nom de cha\u00eene et abonnement{{\/t}}<\/option>\n\t\t\t\t\t<option value=\"creator\">{{#t}}Nom du cr\u00e9ateur{{\/t}}<\/option>\n\t\t\t\t\t<option value=\"created\">{{#t}}Date de cr\u00e9ation (les plus r\u00e9centes en premier){{\/t}}<\/option>\n\t\t\t\t\t<option value=\"members_high\">{{#t}}Membres (du plus grand au plus petit nombre){{\/t}}<\/option>\n\t\t\t\t\t<option value=\"members_low\">{{#t}}Membres (du plus petit au plus grand nombre){{\/t}}<\/option>\n\t\t\t\t<\/select>\n\t\t\t\t{{#feature flag=\"feature_channel_browser_dropdown\"}}\n\t\t\t\t\t<select id=\"temp_select_width\" class=\"display_none\">\n\t\t\t\t\t\t<option id=\"temp_option_width\"><\/option>\n\t\t\t\t\t<\/select>\n\t\t\t\t{{\/feature}}\n\t\t\t<\/div>\n\t\t<\/div>\n\t<\/div>\n\n\t<div id=\"sci_loading\">{{loadingHTML}}<\/div>\n\n\t<div id=\"sci_no_shared_channels\" class=\"display_flex justify_content_between align_items_center hidden\">\n\t\t<span class=\"sci_no_shared_note\">{{#t}}Vous n'avez pas encore de cha\u00eenes partag\u00e9es.{{\/t}}<\/span>\n\t<\/div>\n\n\t<div id=\"sci_channels_container\" class=\"display_flex hidden\">\n\t\t<div class=\"list_container\">\n\t\t\t<div class=\"list\"><\/div>\n\t\t<\/div>\n\t<\/div>\n\n\t<div id=\"sci_channels_create\"><\/div>\n\n\t<div id=\"sci_send\"><\/div>\n\n\t<div id=\"sci_invites_container\"><\/div>\n<\/div>\n";
TS.raw_templates['shared_channels_invites_create'] = "{{i18n_ns \"shared_channels_invite\"}}\n<h1 class=\"sci_header\">{{#t}}Cr\u00e9er une cha\u00eene partag\u00e9e{{\/t}}<\/h1>\n\n<div class=\"channels_create_container\">\n\t<span class=\"sci_sub_header\">{{#if enterprise_info}}\n\t\t{{#t}}Les membres de diff\u00e9rents espaces de travail de {enterprise_info.name} peuvent s'envoyer des messages et partager des fichiers dans une cha\u00eene partag\u00e9e.{{\/t}}\n\t{{else}}\n\t\t{{#t}}Les membres de l'autre espace de travail pourront seulement voir les fichiers et les membres dans cette cha\u00eene partag\u00e9e.{{\/t}}\n\t{{\/if}}<\/span>\n\n\t<label>{{#t note=\"This labels a slider for users to toggle who has access to join or view this channel\"}}Acc\u00e8s pour vos membres{{\/t}}<\/label>\n\t<label for=\"access\"><input type=\"checkbox\" name=\"access\" checked><\/label>\n\n\t{{#if enterprise_info}}\n\t\t<label>{{#t note=\"labels a set of radio buttons to indicate which teams to share the channel with. Could be all org teams, only some org teams, or an external team\"}}Partager cette cha\u00eene avec{{\/t}}<\/label>\n\t\t<label class=\"sci_radio\"><input type=\"radio\" name=\"share_with\" value=\"all\" checked>{{#t}}Tous les espaces de travail de {enterprise_info.name}{{\/t}}<\/label>\n\t\t{{#feature flag=\"feature_connecting_shared_channels_enterprise\"}}\n\t\t\t<label class=\"sci_radio\"><input type=\"radio\" name=\"share_with\" value=\"specific\">{{#t}}Espaces de travail {enterprise_info.name} sp\u00e9cifiques{{\/t}}<\/label>\n\t\t\t<label class=\"sci_radio inline_block\"><input type=\"radio\" name=\"share_with\" value=\"external\">{{#t rough-draft=true}}Espaces de travail externes{{\/t}}<\/label><label for=\"share_with_specific\"><\/label><label for=\"share_with_external\"><\/label>\n\t\t{{else}}\n\t\t\t<label class=\"sci_radio inline_block\"><input type=\"radio\" name=\"share_with\" value=\"specific\">{{#t}}Espaces de travail {enterprise_info.name} sp\u00e