TS.raw_templates['shared_channels_empty_state_for_channel_browser'] = "{{i18n_ns \"shared_channels_invite\"}}\n\n<div class=\"channel_browser_empty_state_for_shared_channels\">\n\t<p class=\"align_center\">\n\t\t{{#if show_pending_shared_channels}}\n\t\t\t{{#t}}<strong>Il n\u2019y a pas de cha\u00eenes partag\u00e9es en attente.<\/strong><br>Les cha\u00eenes partag\u00e9es permettent \u00e0 deux \u00e9quipes Slack de collaborer en toute s\u00e9curit\u00e9. <a href=\"https:\/\/get.slack.help\/hc\/articles\/115004151183\">En savoir plus<\/a>{{\/t}}\n\t\t{{else}}\n\t\t\t{{#t}}Les cha\u00eenes partag\u00e9es vous laissent travailler avec d'autres entreprises et \u00e9quipes directement dans Slack. <a href=\"https:\/\/get.slack.help\/hc\/articles\/115004151183\">En savoir plus<\/a>{{\/t}}\n\t\t{{\/if}}\n\t<\/p>\n\n\t<button class=\"btn btn_outline\" type=\"button\" onclick=\"TS.ui.channel_browser.endAndStartSharedChannelDialog();\">{{#if can_create_shared_channel}}{{#t}}Cr\u00e9er une cha\u00eene partag\u00e9e{{\/t}}{{else}}{{#t}}Demander la cr\u00e9ation d'une cha\u00eene partag\u00e9e{{\/t}}{{\/if}}<\/button>\n<\/div>\n";
TS.raw_templates['shared_channels_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div id=\"shared_channels_coachmark_div\" class=\"coachmark_div\">\n\t<div id=\"coachmark_contents\">\n\t\t<div id=\"shared_channels_coachmark_image\" class=\"bottom_margin {{#if without_animation}} shared_channels_coachmark_image_without_animation{{\/if}}\"><\/div>\n\n\t\t<h3 id=\"shared_channels_coachmark_title\" class=\"black charcoal_grey\">\n\t\t\t{{#t}}Imaginez deux entreprises distinctes qui utilisent Slack pour travailler en collaboration.{{\/t}}\n\t\t<\/h3>\n\t\t<p id=\"shared_channels_coachmar_subtitle\" class=\"large_bottom_margin\">\n\t\t\t{{#t}}Les cha\u00eenes partag\u00e9es vous laissent travailler avec d'autres entreprises et \u00e9quipes directement dans Slack.{{\/t}}\n\t\t<\/p>\n\t\t<div class=\"shared_channels_coachmark_buttons align_right\">\n\t\t\t<button id=\"shared_channels_coachmark_button_got_it\" class=\"btn btn_outline coachmark_skip_link\" type=\"button\">{{#t}}Fermer{{\/t}}<\/button>\n\t\t\t<button id=\"shared_channels_coachmark_button_get_started\" class=\"btn btn_info coachmark_ok\" type=\"button\">{{#if show_create_button}}{{#t}}Cr\u00e9er une cha\u00eene partag\u00e9e{{\/t}}{{else}}{{#t}}Premiers pas{{\/t}}{{\/if}}<\/button>\n\t\t<\/div>\n\t<\/div>\n<\/div>\n";
TS.raw_templates['shared_channels_first_shared_channel_coachmark'] = "{{i18n_ns \"coachmarks\"}}\n\n<div class=\"hidden\">\n\t<h2>{{#t}}Vous avez partag\u00e9 votre premi\u00e8re cha\u00eene{{\/t}}<\/h2>\n\n\t<p>{{#t}}Vous pouvez g\u00e9rer les param\u00e8tres de cette cha\u00eene (comme le partage ou l\u2019annulation du partage) en acc\u00e9dant \u00e0 <strong>Param\u00e8tres de la cha\u00eene \u2192 Options suppl\u00e9mentaires<\/strong>.{{\/t}}<\/p>\n<\/div>\n";
TS.raw_templates['shared_channels_retention_disclaimer'] = "{{i18n_ns 'shared'}}\n\n{{!-- A retention type of 2 or 3 means messages in channels are deleted after a given number of days --}}\n{{#if_gt (teamRetentionType) compare=1}}\n\t<div class=\"p-shared_channels_retention_disclaimer c-alert c-alert--inline c-alert--level_warning bottom_margin\">\n\t\t<ts-icon class=\"c-alert__icon c-deprecated-icon c-icon--warning\"><\/ts-icon>\n\t\t<div class=\"p-shared_channels_retention_disclaimer__message c-alert__message\">\n\t\t\t<strong>{{#t}}Les param\u00e8tres de conservation des messages de votre espace de travail ne s'appliquent pas aux cha\u00eenes partag\u00e9es.{{\/t}}<\/strong>\n\t\t\t<br \/>\n\t\t\t{{#t team_duration=(teamRetentionDuration)}}Les messages de votre espace de travail sont supprim\u00e9s apr\u00e8s {team_duration, plural, =1 {# jour} other {# jours}}. Pendant la phase b\u00eata, ce param\u00e8tre ne s\u2019applique pas aux cha\u00eenes partag\u00e9es.{{\/t}} <a href=\"https:\/\/get.slack.help\/hc\/articles\/115004152843\">{{#t}}En savoir plus{{\/t}}<\/a>\n\t\t<\/div>\n\t<\/div>\n{{\/if_gt}}\n";
TS.raw_templates['shared_channels_miscellaneous'] = "{{i18n_ns 'shared'}}\n\n<div class=\"hidden\">\n\t{{! This appears in the shared channel creation flow. }}\n\t<p>{{#t}}<strong>Remarque\u00a0:<\/strong> pendant la phase b\u00eata, les cha\u00eenes partag\u00e9es ne peuvent \u00eatre que publiques.{{\/t}}<\/p>\n\n\t{{! It's possible we won't support disconnecting shared channels at launch. Just in case, let's put this string in the code to get it translated. }}\n\t<p>{{#t}}Cette op\u00e9ration est irr\u00e9versible.{{\/t}} {{#t}}Vous ne pouvez pour l'instant ni annuler le partage d'une cha\u00eene ni archiver une cha\u00eene partag\u00e9e.{{\/t}}<\/p>\n\n\t{{! Same as above, but not an alert. }}\n\t<p>{{#t}}<strong>Actuellement en version b\u00eata.<\/strong> Certaines des fonctionnalit\u00e9s comme les param\u00e8tres personnalis\u00e9s de conservation des messages ou l'annulation du partage des cha\u00eenes ne sont pas disponibles pendant la phase b\u00eata.{{\/t}} <a href=\"https:\/\/get.slack.help\/hc\/articles\/115004152843\">{{#t}}En savoir plus{{\/t}}<\/a><\/p>\n\n\t{{! Archiving }}\n\t<p>{{#t channel_name=channel_name}}Annuler le partage de {channel_name} et archiver la cha\u00eene{{\/t}}<\/p>\n\t<p>{{#t team_names=team_names}}L'archivage de cette cha\u00eene aura pour effet d'annuler son partage avec {team_names}. Ce qui signifie (en d\u00e9tail)\u00a0:{{\/t}}<\/p>\n\t<p>{{#t note=\"explains consequence of archiving a shared channel: your team gets a copy of the channel that is archived\"}}Votre copie de la cha\u00eene sera archiv\u00e9e{{\/t}}<\/p>\n\t<button>{{#t note=\"action to archive a shared channel (also unshares it)\"}}Annuler le partage et archiver{{\/t}}<\/button>\n\n\t{{! Additional options menu, viewed by a non-admin }}\n\t<p>{{#t}}Seuls les administrateurs peuvent partager une cha\u00eene ou annuler le partage d'une cha\u00eene.{{\/t}}<\/p>\n\t{{! Putting this in its own {t} so we can adjust its styling \/ callback as needed }}\n\t<a>{{#t}}Trouver un administrateur{{\/t}}<\/a>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<p class=\"enterprise_settings_shared_channels_default_channels\">\n\t<h2 class=\"ent_header_secondary\">{{#t note=\"section heading\"}}Cha\u00eenes par d\u00e9faut{{\/t}}<\/h2>\n\n\t<p class=\"permissions_title_info\">{{#t}}Les cha\u00eenes par d\u00e9faut sont les cha\u00eenes auxquelles tous les membres de l\u2019organisation seront ajout\u00e9s.{{\/t}}<\/p>\n\n\t<p class=\"permissions_title_info hidden\" data-js=\"no_more_available_channels\">\n\t\t{{#t}}Aucune cha\u00eene ne peut actuellement \u00eatre d\u00e9finie comme cha\u00eene par d\u00e9faut. Vous devez partager une cha\u00eene sur l\u2019ensemble de votre organisation pour pouvoir ajouter une nouvelle cha\u00eene par d\u00e9faut.{{\/t}}\n\t<\/p>\n\n\t<p class=\"permissions_title_info hidden\" data-js=\"cannot_add_more_channels\">\n\t\t{{#t}}Votre organisation a atteint le nombre maximum de cha\u00eenes par d\u00e9faut disponibles.{{\/t}}\n\t<\/p>\n\n\t<div class=\"display_flex align_items_center top_margin very_small_top_padding\">\n\t\t<h3 data-name=\"default_channels_header\" class=\"ent_header_tertiary flex_one\"><\/h3>\n\n\t\t<div class=\"left_margin\">\n\t\t\t<a data-qa=\"add_default_channel\" class=\"btn btn_small\">{{#t}}Ajouter une cha\u00eene{{\/t}}<\/a>\n\t\t<\/div>\n\t<\/div>\n\n\t<div data-name=\"default_channels\" class=\"top_margin\"><\/div>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_rows'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_channel_row_header display_flex small_top_padding small_bottom_padding left_padding right_padding bottom_border\">\n\t<div class=\"flex_one small_right_margin\">{{#t}}Cha\u00eene{{\/t}}<\/div>\n\t<div class=\"flex_one right_margin very_small_right_padding\">{{#t}}Abonnement{{\/t}}<\/div>\n<\/div>\n\n{{#unless default_channels}}\n\t<div class=\"default_channel_row_no_channels large_padding align_center\">\n\t\t{{#t}}Il n'y a encore aucune cha\u00eene sugg\u00e9r\u00e9e ou requise.{{\/t}}\n\t<\/div>\n{{\/unless}}\n\n{{#each default_channels}}\n\t<div class=\"default_channel_row display_flex normal_padding {{#if @last}}{{else}}bottom_border{{\/if}}\" data-id=\"{{id}}\" data-popover-trigger=\"{{id}}\">\n\t\t<div class=\"default_channel_name flex_one small_right_margin bold\">\n\t\t\t<ts-icon class=\"ts_icon_channel ts_icon_inherit\"><\/ts-icon>{{name}} <ts-icon class=\"ts_icon_org_shared_channel ts_icon_inherit\"><\/ts-icon>\n\t\t<\/div>\n\t\t<div class=\"default_channel_mandatory flex_one\">\n\t\t\t{{#if is_mandatory}}\n\t\t\t\t<span class=\"tiny_negative_left_margin\">\n\t\t\t\t\t<ts-icon class=\"ts_icon_lock ts_icon_inherit\"><\/ts-icon> <strong>{{#t}}N\u00e9cessaire{{\/t}}<\/strong>\n\t\t\t\t<\/span>\n\t\t\t{{else}}\n\t\t\t\t{{#t}}Facultatif{{\/t}}\n\t\t\t{{\/if}}\n\t\t<\/div>\n\t\t<div class=\"default_channel_actions\">\n\t\t\t<span data-qa=\"default_channel_menu\" class=\"cursor_pointer\"><ts-icon class=\"ts_icon_ellipsis nudge_top_3\"><\/ts-icon><\/span>\n\t\t<\/div>\n\t<\/div>\n{{\/each}}\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_new_channel'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t{{#feature flag=\"feature_default_shared_channels\"}}\n\t\t<p>{{#t}}Tous les membres r\u00e9guliers de\/d\u2019 {org_name} seront automatiquement ajout\u00e9s \u00e0 cette cha\u00eene. Les utilisateurs invit\u00e9s devront \u00eatre ajout\u00e9s manuellement.{{\/t}}<\/p>\n\n\t\t<h3 class=\"ent_header_tertiary bold margin_bottom_75 black\">{{#t}}S\u00e9lectionner une cha\u00eene{{\/t}}<\/h3>\n\n\t\t<p data-js=\"no_more_available_channels\" class=\"hidden margin_bottom_100\">\n\t\t\t{{#t}}Aucune cha\u00eene ne peut actuellement \u00eatre d\u00e9finie comme cha\u00eene par d\u00e9faut. Vous devez partager une cha\u00eene sur l\u2019ensemble de votre organisation pour pouvoir ajouter une nouvelle cha\u00eene par d\u00e9faut.{{\/t}}\n\t\t<\/p>\n\n\t\t<div id=\"global_channel_picker\"><\/div>\n\n\t\t<p class=\"c-alert c-alert--boxed c-alert--level_info margin_top_150\">\n\t\t\t<i class=\"c-alert__icon c-deprecated-icon c-icon--info-circle c-deprecated-icon--vertical-align-baseline\"><\/i>\n\t\t\t<span class=\"c-alert__message\">{{#t learn_more_link=\"https:\/\/get.slack.help\/hc\/en-us\/articles\/115001399587-Create-a-shared-channel\"}}Les cha\u00eenes par d\u00e9faut doivent \u00eatre des cha\u00eenes existantes d\u00e9j\u00e0 partag\u00e9es avec l\u2019ensemble de votre organisation. <a href=\"{learn_more_link}\">En savoir plus sur le partage de cha\u00eenes.<\/a>{{\/t}}<\/span>\n\t\t<\/p>\n\t{{\/feature}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_new_channel_type_and_perms'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t{{> enterprise_settings_shared_channels_default_channels_modal_membership_options}}\n\n\t{{> enterprise_settings_shared_channels_default_channels_modal_permissions}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_add_all_members'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t{{#feature flag=\"feature_default_shared_channels\"}}\n\t\t<p class=\"border_bottom padding_bottom_150\">{{#t}}Faire de {channel_name} une cha\u00eene par d\u00e9faut pour tous les membres de {org_name}\u00a0?{{\/t}}<\/p>\n\n\t\t<p class=\"display_flex border_bottom padding_top_50 padding_bottom_150 margin_left_75\"><i class=\"c-deprecated-icon c-icon--user sk_fill_blue\"><\/i> <span class=\"margin_left_100\">{{#t}}<strong>{member_total} membres<\/strong> seront ajout\u00e9s \u00e0 cette cha\u00eene. Les futurs membres r\u00e9guliers seront automatiquement ajout\u00e9s \u00e0 cette cha\u00eene.{{\/t}}<\/span><\/p>\n\n\t\t<p class=\"display_flex border_bottom padding_top_50 padding_bottom_150 margin_left_75\"><i class=\"c-deprecated-icon c-icon--clock-o sk_pin_orange\"><\/i> <span class=\"margin_left_100\">{{#t}}L\u2019ajout de tous les membres \u00e0 la cha\u00eene peut prendre <strong>jusqu\u2019\u00e0 {time_to_completion_in_minutes}\u00a0minutes<\/strong>. Un message sera publi\u00e9 sur la cha\u00eene une fois l\u2019op\u00e9ration termin\u00e9e.{{\/t}}<\/span><\/p>\n\n\t\t{{#if is_mandatory}}\n\t\t\t<p class=\"display_flex border_bottom padding_top_50 padding_bottom_150 margin_left_75\"><i class=\"c-deprecated-icon c-icon--lock-o sk_warning_red\"><\/i> <span class=\"margin_left_100\">{{#t}}L\u2019adh\u00e9sion \u00e9tant <strong>obligatoire<\/strong>, les membres ne pourront pas quitter la cha\u00eene.{{\/t}}<\/span><\/p>\n\t\t{{\/if}}\n\n\t\t{{#if can_thread}}\n\t\t\t<p class=\"display_flex border_bottom padding_top_50 padding_bottom_150 margin_left_75\"><i class=\"c-deprecated-icon c-icon--comment-alt sk_seafoam_green\"><\/i> <span class=\"margin_left_100\">{{#t users=(listify selected_users strong=true)}}Seuls les {users} pourront publier des contenus dans cette cha\u00eene. Tous les membres pourront r\u00e9pondre aux messages en utilisant les fils de discussion.{{\/t}}<\/span><\/p>\n\t\t{{else}}\n\t\t\t<p class=\"display_flex border_bottom padding_top_50 padding_bottom_150 margin_left_75\"><i class=\"c-deprecated-icon c-icon--comment-alt sk_seafoam_green\"><\/i> <span class=\"margin_left_100\">{{#t users=(listify selected_users strong=true)}}Seuls les {users} pourront publier des contenus dans cette cha\u00eene.{{\/t}}<\/span><\/p>\n\t\t{{\/if}}\n\t{{\/feature}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_edit_membership'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t<p>{{#t}}Vous pouvez choisir entre un abonnement facultatif ou obligatoire pour cette cha\u00eene par d\u00e9faut. Tous les membres de la cha\u00eene resteront abonn\u00e9s.{{\/t}}<\/p>\n\n\t{{> enterprise_settings_shared_channels_default_channels_modal_membership_options}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_membership_options'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<h3 class=\"ent_header_tertiary large_top_margin bold margin_bottom_50 black\">{{#t}}Choisissez le type d'abonnement{{\/t}}<\/h3>\n<p class=\"margin_bottom_100\">{{#t}}Choisissez si vous souhaitez que l'abonnement soit simplement propos\u00e9 ou obligatoire pour tous les membres.{{\/t}}<\/p>\n\n<div class=\"display_flex justify_content_between\">\n\t<div class=\"flex_one margin_right_75 cursor_pointer normal_padding {{#if is_mandatory}}{{else}}active{{\/if}}\" data-qa=\"new_channel_radio\">\n\t\t<div class=\"no_bottom_margin display_flex\">\n\t\t\t<input id=\"optional\" class=\"nudge_bottom_2 margin_top_50\" type=\"radio\" name=\"membership_type\" value=\"default\" {{#if is_mandatory}}{{else}}checked{{\/if}} \/>\n\t\t\t<div class=\"margin_left_50 display_flex flex_direction_column\">\n\t\t\t\t<label for=\"optional\" class=\"no_bottom_margin\"><p class=\"no_bottom_margin\">{{#t}}Facultatif{{\/t}}<\/p><\/label>\n\t\t\t\t<p class=\"no_bottom_margin\">{{#t}}Les membres peuvent quitter la cha\u00eene.{{\/t}}<\/p>\n\t\t\t<\/div>\n\t\t<\/div>\n\t<\/div>\n\n\t<div class=\"flex_one cursor_pointer normal_padding {{#if is_mandatory}}active{{\/if}}\" data-qa=\"new_channel_radio\">\n\t\t<div class=\"no_bottom_margin display_flex\">\n\t\t\t<input id=\"mandatory\" class=\"nudge_bottom_2 margin_top_50\" type=\"radio\" name=\"membership_type\" value=\"mandatory\" {{#if is_mandatory}}checked{{\/if}} \/>\n\t\t\t<div class=\"margin_left_50 display_flex flex_direction_column\">\n\t\t\t\t<label for=\"mandatory\" class=\"no_bottom_margin\"><p class=\"no_bottom_margin\"><ts-icon class=\"ts_icon_lock ts_icon_inherit nudge_right_3 nudge_bottom_2\"><\/ts-icon>{{#t}}N\u00e9cessaire{{\/t}}<\/p><\/label>\n\t\t\t\t<p class=\"no_bottom_margin\">{{#t}}Les membres ne pourront <strong>pas<\/strong> partir.{{\/t}}<\/p>\n\t\t\t<\/div>\n\t\t<\/div>\n\t<\/div>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_permissions'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n{{#feature flag=\"feature_default_shared_channels\"}}\n\n<h3 class=\"ent_header_tertiary margin_top_250 bold margin_bottom_50 black\">{{#t}}Autorisations de publication{{\/t}}<\/h3>\n<p class=\"margin_bottom_50\">{{#t}}Qui peut publier sur cette cha\u00eene\u00a0?{{\/t}}<\/p>\n\n<div>\n\t{{> enterprise_settings_shared_channels_default_channels_modal_new_channel_perms_radios}}\n<\/div>\n\n<div id=\"lfs_default_channel_posting_permissions_picker_wrapper\" class=\"hidden left_margin small_padding\">\n\t<input id=\"lfs_default_channel_posting_permissions_picker\" class=\"no_bottom_margin\" \/>\n<\/div>\n\n<div class=\"margin_top_75 nudge_right_1 position_relative\">\n\t<label class=\"c-label c-label--inline c-label--pointer\" for=\"default_channels_can_thread\">\n    <input type=\"checkbox\" class=\"c-input_checkbox\" id=\"default_channels_can_thread\" {{#if can_thread}}checked{{\/if}} \/>\n    <span class=\"c-label__text\">{{#t}}Autoriser tous les membres \u00e0 utiliser les fils de discussion{{\/t}}<\/span>\n\t<\/label>\n\t<p class=\"margin_left_100 padding_left_75\">{{#t}}Si cette option est s\u00e9lectionn\u00e9e, tous les membres pourront ajouter des r\u00e9ponses sous forme de fils de discussion aux messages publi\u00e9s dans cette cha\u00eene\u00a0; et ceci, quelles que soient leurs autorisations de publication.{{\/t}}<\/p>\n<\/div>\n\n{{\/feature}}\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_make_default'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t<p>{{#t}}Une fois l'abonnement de <strong>{channel_name}<\/strong> rendu facultatif, de nouveaux membres de l'organisation continueront d'y \u00eatre automatiquement ajout\u00e9s, mais tout le monde pourra quitter la cha\u00eene.{{\/t}}<\/p>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_make_mandatory'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t<p>{{#t}}Une fois l'abonnement rendu obligatoire dans <strong>{channel_name}<\/strong>, personne ne pourra quitter la cha\u00eene. Cela ajoutera aussi de fa\u00e7on automatique tout membre de l'organisation qui ne s'y trouve pas encore.{{\/t}}<\/p>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_remove_channel'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n<div class=\"default_shared_channels_modal_component clearfix large_top_margin margin_auto\">\n\t<p>{{#t}}En supprimant <strong>{channel_name}<\/strong> des cha\u00eenes sugg\u00e9r\u00e9es et obligatoires, les nouveaux membres de l'organisation ne seront plus automatiquement ajout\u00e9s \u00e0 la cha\u00eene.{{\/t}}<\/p>\n\n\t<p>{{#t}}Tout membre qui fait actuellement partie de la cha\u00eene restera, mais il pourra quitter la cha\u00eene s'il le souhaite.{{\/t}}<\/p>\n\n\t<p>{{#t}}Voulez-vous vraiment supprimer <strong>{channel_name}<\/strong> des cha\u00eenes sugg\u00e9r\u00e9es et obligatoires\u00a0?{{\/t}}<\/p>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_channel_row'] = "{{~#if item.private~}}\n\t<ts-icon class=\"ts_icon_lock ts_icon_inherit\"><\/ts-icon>\n{{~else~}}\n\t<ts-icon class=\"ts_icon_channel ts_icon_inherit\"><\/ts-icon>\n{{~\/if~}}\n{{~item.name}} {{> shared_channel_icon}}\n";
TS.raw_templates['enterprise_settings_shared_channels_default_channels_modal_new_channel_perms_radios'] = "{{i18n_ns \"enterprise_dashboard\"}}\n\n{{#feature flag=\"feature_default_shared_channels\"}}\n\n<label class=\"radio modify_options\">\n\t<input name=\"default_channels_who_can_post\" type=\"radio\" value=\"owner\" {{#if_equal who_can_post_type compare=\"owner\"}}checked=\"checked\"{{\/if_equal}}>\n\t<span class=\"nudge_left_3 position_relative\">{{#t}}Propri\u00e9taires et administrateurs de l\u2019organisation{{\/t}}<\/span>\n<\/label>\n\n<label class=\"radio modify_options\">\n\t<input name=\"default_channels_who_can_post\" type=\"radio\" value=\"multi\" id=\"multi_manage_selector\" {{#if_equal who_can_post_type compare=\"multi\"}}checked=\"checked\"{{\/if_equal}}>\n\t<span class=\"nudge_left_3 position_relative\">{{#t}}Les propri\u00e9taires et administrateurs de l\u2019organisation, plus certains membres s\u00e9lectionn\u00e9s{{\/t}}<\/span>\n<\/label>\n\n{{\/feature}}\n";
TS.raw_templates['keyboard_shortcuts_prompt'] = "{{i18n_ns \"keyboard_shortcuts\"}}\n{{#feature flag=\"feature_shortcuts_prompt\"}}\n\t<div class=\"p-shortcuts_prompt__body modal-body\" role=\"document\">\n\t\t<div class=\"p-shortcuts_prompt__shortcuts\">\n\t\t\t<p class=\"p-shortcuts_prompt__shortcuts_intro\">\n\t\t\t\t{{#t}}Les raccourcis vous permettent de naviguer plus facilement dans l\u2019environnement Slack. Voici quelques raccourcis qui pourraient vous \u00eatre utiles\u00a0:{{\/t}}\n\t\t\t<\/p>\n\t\t\t<ul class=\"p-shortcuts_prompt__shortcuts_list\">\n\t\t\t\t<li class=\"p-shortcuts_prompt__shortcut\">\n\t\t\t\t\t<span class=\"c-keyboard_key p-shortcuts_prompt__arrow_key\"><ts-icon class=\"ts_icon_arrow_up_medium\"><\/ts-icon><\/span>\n\t\t\t\t\t<span class=\"c-keyboard_key p-shortcuts_prompt__arrow_key\"><ts-icon class=\"ts_icon_arrow_down_medium\"><\/ts-icon><\/span>\n\t\t\t\t\t<span class=\"shortcut_text\">{{#t}} Passer d\u2019un message \u00e0 l\u2019autre{{\/t}}<\/span>\n\t\t\t\t<\/li>\n\t\t\t\t<li class=\"p-shortcuts_prompt__shortcut\">\n\t\t\t\t\t<span class=\"c-keyboard_key\">{{#t}}Ctrl{{\/t}}<\/ts-icon><\/span>\n\t\t\t\t\t<span class=\"c-keyboard_key\">{{backtick_key}}<\/ts-icon><\/span>\n\t\t\t\t\t<span class=\"shortcut_text\">{{#t}} Passer \u00e0 la section suivante{{\/t}}<\/span>\n\t\t\t\t<\/li>\n\t\t\t\t<li class=\"p-shortcuts_prompt__shortcut\">\n\t\t\t\t\t<span class=\"c-keyboard_key\">{{#isMac}}{{#t}}Cmd{{\/t}}{{else}}{{#t}}Ctrl{{\/t}}{{\/isMac}}<\/ts-icon><\/span>\n\t\t\t\t\t<span class=\"c-keyboard_key\">{{#t}}K{{\/t}}<\/ts-icon><\/span>\n\t\t\t\t\t<span class=\"p-shortcuts_prompt__shortcut_text\">{{#t}}Commutateur{{\/t}}<\/span>\n\t\t\t\t<\/li>\n\t\t\t<\/ul>\n\n\t\t\t<p class=\"p-shortcuts_prompt__shortcuts_intro\">\n\t\t\t\t{{#t}}Et ce n\u2019est que le d\u00e9but. Pour afficher la liste compl\u00e8te des raccourcis clavier, appuyez simplement sur <span class=\"c-keyboard_key\">{cmd_key}<\/span> + <span class=\"c-keyboard_key\">{slash_key}<\/span>{{\/t}}\n\t\t\t<\/p>\n\t\t<\/div>\n\t<\/div>\n{{\/feature}}";
TS.raw_templates['email_prefs_updated'] = "{{i18n_ns \"email_prefs\"}}\n\n{{#feature flag=\"feature_email_prefs\"}}\n<div class=\"email_prefs__header\"><\/div>\n\t<div class=\"email_prefs__body align_center\">\n\t\t<div class=\"bottom_margin seafoam_green\"><ts-icon class=\"ts_icon_large ts_icon_check_circle_o_large\"><\/ts-icon><\/div>\n\t\t\n\n\t\t{{#if num_email_subs}}\n\t\t\t<h1>{{#t}}Vos pr\u00e9f\u00e9rences ont \u00e9t\u00e9 mises \u00e0 jour{{\/t}}<\/h1>\n\t\t\t{{#if email_sub_text_array}}\n\t\t\t\t<div>{{#t note=\"the types of emails they're subscribed to\" list_of_email_subs=(listify email_sub_text_array)}}Vous avez choisi de recevoir\u00a0: {list_of_email_subs}.{{\/t}}<\/div>\n\t\t\t{{\/if}}\n\t\t{{else}}\n\t\t\t<h1>{{#t}}Vous vous \u00eates d\u00e9sabonn\u00e9(e) de tous les e-mails de Slack{{\/t}}<\/h1>\n\n\t\t\t<div>{{#t}}Vous ne recevrez plus d\u2019e-mails marketing de la part de Slack, mais vous recevrez toujours les messages importants (comme les demandes de r\u00e9initialisation du mot de passe).{{\/t}}<\/div>\n\t\t{{\/if}}\n\n\t<\/div>\n<div class=\"email_prefs__footer\"><\/div>\n\n{{\/feature}}";
